     * @param value Ether value.
     * @param data Data payload.
     * @param operation Operation type.
     * @param safeTxGas Gas that should be used for the safe transaction.
     * @param baseGas Gas costs for that are independent of the transaction execution(e.g. base transaction fee, signature check, payment of the refund)
     * @param gasPrice Maximum gas price that should be used for this transaction.
     * @param gasToken Token address (or 0 if ETH) that is used for the payment.
     * @param refundReceiver Address of receiver of gas payment (or 0 if tx.origin).
     * @param _nonce Transaction nonce.
     * @return Transaction hash bytes.
     */
    function encodeTransactionData(
        address to,
        uint256 value,
        bytes calldata data,
        Enum.Operation operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address refundReceiver,
        uint256 _nonce
    ) public view returns (bytes memory) {
        bytes32 safeTxHash = keccak256(
            abi.encode(
                SAFE_TX_TYPEHASH,
                to,
                value,
                keccak256(data),
                operation,
                safeTxGas,
                baseGas,
                gasPrice,
                gasToken,
                refundReceiver,
                _nonce
            )
        );
        return abi.encodePacked(bytes1(0x19), bytes1(0x01), domainSeparator(), safeTxHash);
    }

    /**
     * @notice Returns transaction hash to be signed by owners.
     * @param to Destination address.
     * @param value Ether value.
     * @param data Data payload.
     * @param operation Operation type.
     * @param safeTxGas Fas that should be used for the safe transaction.
     * @param baseGas Gas costs for data used to trigger the safe transaction.
     * @param gasPrice Maximum gas price that should be used for this transaction.
     * @param gasToken Token address (or 0 if ETH) that is used for the payment.
     * @param refundReceiver Address of receiver of gas payment (or 0 if tx.origin).
     * @param _nonce Transaction nonce.
     * @return Transaction hash.
     */
    function getTransactionHash(
        address to,
        uint256 value,
        bytes calldata data,
        Enum.Operation operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address refundReceiver,
        uint256 _nonce
    ) public view returns (bytes32) {
        return keccak256(encodeTransactionData(to, value, data, operation, safeTxGas, baseGas, gasPrice, gasToken, refundReceiver, _nonce));
    }
}

// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity >=0.7.0 <0.9.0;

import "./Safe.sol";

/**
 * @title SafeL2 - An implementation of the Safe contract that emits additional events on transaction executions.
 * @notice For a more complete description of the Safe contract, please refer to the main Safe contract `Safe.sol`.
 * @author Stefan George - @Georgi87
 * @author Richard Meissner - @rmeissner
 */
contract SafeL2 is Safe {
    event SafeMultiSigTransaction(
        address to,
        uint256 value,
        bytes data,
        Enum.Operation operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address payable refundReceiver,
        bytes signatures,
        // We combine nonce, sender and threshold into one to avoid stack too deep
        // Dev note: additionalInfo should not contain `bytes`, as this complicates decoding
        bytes additionalInfo
    );

    event SafeModuleTransaction(address module, address to, uint256 value, bytes data, Enum.Operation operation);

    // @inheritdoc Safe
    function execTransaction(
        address to,
        uint256 value,
        bytes calldata data,
        Enum.Operation operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address payable refundReceiver,
        bytes memory signatures
    ) public payable override returns (bool) {
        bytes memory additionalInfo;
        {
            additionalInfo = abi.encode(nonce, msg.sender, threshold);
        }
        emit SafeMultiSigTransaction(
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver,
            signatures,
            additionalInfo
        );
        return super.execTransaction(to, value, data, operation, safeTxGas, baseGas, gasPrice, gasToken, refundReceiver, signatures);
    }

    // @inheritdoc Safe
    function execTransactionFromModule(
        address to,
        uint256 value,
        bytes memory data,
        Enum.Operation operation
    ) public override returns (bool success) {
        emit SafeModuleTransaction(msg.sender, to, value, data, operation);
        success = super.execTransactionFromModule(to, value, data, operation);
    }
}

contracts/core/BorrowerOperations.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/ITroveManager.sol";
import "../interfaces/IBorrowerOperations.sol";
import "../interfaces/IZebraUSD.sol";
import "../dependencies/ZebraBase.sol";
import "../dependencies/ZebraMath.sol";
import "../dependencies/ZebraOwnable.sol";
import "../dependencies/DelegatedOps.sol";
import "../dependencies/console.sol";

/**
    @title Zebra Borrower Operations
    @notice Based on Liquity's `BorrowerOperations`
            https://github.com/liquity/dev/blob/main/packages/contracts/contracts/BorrowerOperations.sol

            Zebra's implementation is modified to support multiple collaterals. There is a 1:n
            relationship between `BorrowerOperations` and each `TroveManager` / `SortedTroves` pair.
 */
contract BorrowerOperations is IBorrowerOperations, ZebraBase, ZebraOwnable, DelegatedOps {
	using SafeERC20 for IERC20;

	IZebraUSD public immutable zebraUSD;
	address public immutable factory;
	uint256 public minNetDebt;

	mapping(ITroveManager => TroveManagerData) public troveManagersData;
	ITroveManager[] internal _troveManagers;

	struct TroveManagerData {
		IERC20 collateralToken;
		uint16 index;
	}

	struct LocalVariables_adjustTrove {
		uint256 price;
		uint256 totalPricedCollateral;
		uint256 totalDebt;
		uint256 collChange;
		uint256 netDebtChange;
		bool isCollIncrease;
		uint256 debt;
		uint256 coll;
		uint256 newDebt;
		uint256 newColl;
		uint256 stake;
		uint256 debtChange;
		address account;
		uint256 MCR;
	}

	struct LocalVariables_openTrove {
		uint256 price;
		uint256 totalPricedCollateral;
		uint256 totalDebt;
		uint256 netDebt;
		uint256 compositeDebt;
		uint256 ICR;
		uint256 NICR;
		uint256 stake;
		uint256 arrayIndex;
	}

	constructor(IZebraCore _zebraCore, address _zebraUSDAddress, address _factory, uint256 _minNetDebt, uint256 _gasCompensation) ZebraOwnable(_zebraCore) ZebraBase(_gasCompensation) {
		zebraUSD = IZebraUSD(_zebraUSDAddress);
		factory = _factory;
		_setMinNetDebt(_minNetDebt);
	}

	function setMinNetDebt(uint256 _minNetDebt) public onlyOwner {
		_setMinNetDebt(_minNetDebt);
	}

	function _setMinNetDebt(uint256 _minNetDebt) internal {
		require(_minNetDebt > 0, "invalid min net debt");
		minNetDebt = _minNetDebt;
	}

	function configureCollateral(ITroveManager troveManager, IERC20 collateralToken) external {
		require(msg.sender == factory, "!factory");
		troveManagersData[troveManager] = TroveManagerData(collateralToken, uint16(_troveManagers.length));
		_troveManagers.push(troveManager);
		emit CollateralConfigured(troveManager, collateralToken);
	}

	function removeTroveManager(ITroveManager troveManager) external {
		TroveManagerData memory tmData = troveManagersData[troveManager];
		require(address(tmData.collateralToken) != address(0) && troveManager.sunsetting() && troveManager.getEntireSystemDebt() == 0, "Trove Manager cannot be removed");
		delete troveManagersData[troveManager];
		uint256 lastIndex = _troveManagers.length - 1;
		if (tmData.index < lastIndex) {
			ITroveManager lastTm = _troveManagers[lastIndex];
			_troveManagers[tmData.index] = lastTm;
			troveManagersData[lastTm].index = tmData.index;
		}

		_troveManagers.pop();
		emit TroveManagerRemoved(troveManager);
	}

	/**
        @notice Get the global total collateral ratio
        @dev Not a view because fetching from the oracle is state changing.
             Can still be accessed as a view from within the UX.
     */
	function getTCR() external returns (uint256 globalTotalCollateralRatio) {
		SystemBalances memory balances = fetchBalances();
		(globalTotalCollateralRatio, , ) = _getTCRData(balances);
		return globalTotalCollateralRatio;
	}

	/**
        @notice Get total collateral and debt balances for all active collaterals, as well as
                the current collateral prices
        @dev Not a view because fetching from the oracle is state changing.
             Can still be accessed as a view from within the UX.
     */
	function fetchBalances() public returns (SystemBalances memory balances) {
		uint256 loopEnd = _troveManagers.length;
		balances = SystemBalances({ collaterals: new uint256[](loopEnd), debts: new uint256[](loopEnd), prices: new uint256[](loopEnd) });
		for (uint256 i; i < loopEnd; ) {
			ITroveManager troveManager = _troveManagers[i];
			(uint256 collateral, uint256 debt, uint256 price) = troveManager.getEntireSystemBalances();
			balances.collaterals[i] = collateral;
			balances.debts[i] = debt;
			balances.prices[i] = price;
			unchecked {
				++i;
			}
		}
	}

	function checkRecoveryMode(uint256 TCR) public pure returns (bool) {
		return TCR < CCR;
	}

	function getCompositeDebt(uint256 _debt) external view returns (uint256) {
		return _getCompositeDebt(_debt);
	}

	// --- Borrower Trove Operations ---
	function openTrove(ITroveManager troveManager, address account, uint256 _maxFeePercentage, uint256 _collateralAmount, uint256 _debtAmount, address _upperHint, address _lowerHint) external callerOrDelegated(account) {
		require(!ZebraCore.paused(), "Deposits are paused");
		troveManager.distributeInterestDebt();
		IERC20 collateralToken;
		LocalVariables_openTrove memory vars;
		bool isRecoveryMode;
		(collateralToken, vars.price, vars.totalPricedCollateral, vars.totalDebt, isRecoveryMode) = _getCollateralAndTCRData(troveManager);
		_requireValidMaxFeePercentage(_maxFeePercentage);

		vars.netDebt = _debtAmount;

		if (!isRecoveryMode) {
			vars.netDebt = vars.netDebt + _triggerBorrowingFee(troveManager, account, _maxFeePercentage, _debtAmount);
		}
		_requireAtLeastMinNetDebt(vars.netDebt);

		// ICR is based on the composite debt, i.e. the requested Debt amount + Debt borrowing fee + Debt gas comp.
		vars.compositeDebt = _getCompositeDebt(vars.netDebt);
		vars.ICR = ZebraMath._computeCR(_collateralAmount, vars.compositeDebt, vars.price);
		vars.NICR = ZebraMath._computeNominalCR(_collateralAmount, vars.compositeDebt);

		if (isRecoveryMode) {
			_requireICRisAboveCCR(vars.ICR);
		} else {
			_requireICRisAboveMCR(vars.ICR, troveManager.MCR());
			uint256 newTCR = _getNewTCRFromTroveChange(vars.totalPricedCollateral, vars.totalDebt, _collateralAmount * vars.price, true, vars.compositeDebt, true); // bools: coll increase, debt increase
			_requireNewTCRisAboveCCR(newTCR);
		}

		// Create the trove
		(vars.stake, vars.arrayIndex) = troveManager.openTrove(account, _collateralAmount, vars.compositeDebt, vars.NICR, _upperHint, _lowerHint);
		emit TroveCreated(account, vars.arrayIndex);

		// Move the collateral to the Trove Manager
		collateralToken.safeTransferFrom(msg.sender, address(troveManager), _collateralAmount);

		//  and mint the DebtAmount to the caller and gas compensation for Gas Pool
		zebraUSD.mintWithGasCompensation(msg.sender, _debtAmount);

		emit TroveUpdated(account, vars.compositeDebt, _collateralAmount, vars.stake, BorrowerOperation.openTrove);
	}

	// Send collateral to a trove
	function addColl(ITroveManager troveManager, address account, uint256 _collateralAmount, address _upperHint, address _lowerHint) external callerOrDelegated(account) {
		require(!ZebraCore.paused(), "Trove adjustments are paused");
		_adjustTrove(troveManager, account, 0, _collateralAmount, 0, 0, false, _upperHint, _lowerHint);
	}

	// Withdraw collateral from a trove
	function withdrawColl(ITroveManager troveManager, address account, uint256 _collWithdrawal, address _upperHint, address _lowerHint) external callerOrDelegated(account) {
		_adjustTrove(troveManager, account, 0, 0, _collWithdrawal, 0, false, _upperHint, _lowerHint);
	}

	// Withdraw Debt tokens from a trove: mint new Debt tokens to the owner, and increase the trove's debt accordingly
	function withdrawDebt(ITroveManager troveManager, address account, uint256 _maxFeePercentage, uint256 _debtAmount, address _upperHint, address _lowerHint) external callerOrDelegated(account) {
		require(!ZebraCore.paused(), "Withdrawals are paused");
		_adjustTrove(troveManager, account, _maxFeePercentage, 0, 0, _debtAmount, true, _upperHint, _lowerHint);
	}

	// Repay Debt tokens to a Trove: Burn the repaid Debt tokens, and reduce the trove's debt accordingly
	function repayDebt(ITroveManager troveManager, address account, uint256 _debtAmount, address _upperHint, address _lowerHint) external callerOrDelegated(account) {
		_adjustTrove(troveManager, account, 0, 0, 0, _debtAmount, false, _upperHint, _lowerHint);
	}

	function adjustTrove(ITroveManager troveManager, address account, uint256 _maxFeePercentage, uint256 _collDeposit, uint256 _collWithdrawal, uint256 _debtChange, bool _isDebtIncrease, address _upperHint, address _lowerHint) external callerOrDelegated(account) {
		require((_collDeposit == 0 && !_isDebtIncrease) || !ZebraCore.paused(), "Trove adjustments are paused");
		require(_collDeposit == 0 || _collWithdrawal == 0, "BorrowerOperations: Cannot withdraw and add coll");
		_adjustTrove(troveManager, account, _maxFeePercentage, _collDeposit, _collWithdrawal, _debtChange, _isDebtIncrease, _upperHint, _lowerHint);
	}

	function _adjustTrove(ITroveManager troveManager, address account, uint256 _maxFeePercentage, uint256 _collDeposit, uint256 _collWithdrawal, uint256 _debtChange, bool _isDebtIncrease, address _upperHint, address _lowerHint) internal {
		require(_collDeposit != 0 || _collWithdrawal != 0 || _debtChange != 0, "BorrowerOps: There must be either a collateral change or a debt change");
		troveManager.distributeInterestDebt();
		IERC20 collateralToken;
		LocalVariables_adjustTrove memory vars;
		bool isRecoveryMode;
		(collateralToken, vars.price, vars.totalPricedCollateral, vars.totalDebt, isRecoveryMode) = _getCollateralAndTCRData(troveManager);

		(vars.coll, vars.debt) = troveManager.applyPendingRewards(account);
		troveManager.repayInterest(msg.sender, account, vars.debt);

		// Get the collChange based on whether or not collateral was sent in the transaction
		(vars.collChange, vars.isCollIncrease) = _getCollChange(_collDeposit, _collWithdrawal);
		vars.netDebtChange = _debtChange;
		vars.debtChange = _debtChange;
		vars.account = account;
		vars.MCR = troveManager.MCR();

		if (_isDebtIncrease) {
			require(_debtChange > 0, "BorrowerOps: Debt increase requires non-zero debtChange");
			_requireValidMaxFeePercentage(_maxFeePercentage);
			if (!isRecoveryMode) {
				// If the adjustment incorporates a debt increase and system is in Normal Mode, trigger a borrowing fee
				vars.netDebtChange += _triggerBorrowingFee(troveManager, msg.sender, _maxFeePercentage, _debtChange);
			}
		}

		// Calculate old and new ICRs and check if adjustment satisfies all conditions for the current system mode
		_requireValidAdjustmentInCurrentMode(vars.totalPricedCollateral, vars.totalDebt, isRecoveryMode, _collWithdrawal, _isDebtIncrease, vars);

		// When the adjustment is a debt repayment, check it's a valid amount and that the caller has enough Debt
		if (!_isDebtIncrease && _debtChange > 0) {
			_requireAtLeastMinNetDebt(_getNetDebt(vars.debt) - vars.netDebtChange);
		}

		// If we are incrasing collateral, send tokens to the trove manager prior to adjusting the trove
		if (vars.isCollIncrease) collateralToken.safeTransferFrom(msg.sender, address(troveManager), vars.collChange);

		(vars.newColl, vars.newDebt, vars.stake) = troveManager.updateTroveFromAdjustment(_isDebtIncrease, vars.debtChange, vars.netDebtChange, vars.isCollIncrease, vars.collChange, _upperHint, _lowerHint, vars.account, msg.sender);

		emit TroveUpdated(vars.account, vars.newDebt, vars.newColl, vars.stake, BorrowerOperation.adjustTrove);
	}

	function closeTrove(ITroveManager troveManager, address account) external callerOrDelegated(account) {
		IERC20 collateralToken;

		uint256 price;
		bool isRecoveryMode;
		uint256 totalPricedCollateral;
		uint256 totalDebt;
		(collateralToken, price, totalPricedCollateral, totalDebt, isRecoveryMode) = _getCollateralAndTCRData(troveManager);
		require(!isRecoveryMode, "BorrowerOps: Operation not permitted during Recovery Mode");
		troveManager.distributeInterestDebt();
		(uint256 coll, uint256 debt) = troveManager.applyPendingRewards(account);
		troveManager.repayInterest(msg.sender, account, debt);
		uint256 newTCR = _getNewTCRFromTroveChange(totalPricedCollateral, totalDebt, coll * price, false, debt, false);
		_requireNewTCRisAboveCCR(newTCR);

		troveManager.closeTrove(account, msg.sender, coll, debt);

		emit TroveUpdated(account, 0, 0, 0, BorrowerOperation.closeTrove);

		// Burn the repaid Debt from the user's balance and the gas compensation from the Gas Pool
		zebraUSD.burnWithGasCompensation(msg.sender, debt - DEBT_GAS_COMPENSATION);
	}

	// --- Helper functions ---

	function _triggerBorrowingFee(ITroveManager _troveManager, address _caller, uint256 _maxFeePercentage, uint256 _debtAmount) internal returns (uint256) {
		uint256 debtFee = _troveManager.decayBaseRateAndGetBorrowingFee(_debtAmount);

		_requireUserAcceptsFee(debtFee, _debtAmount, _maxFeePercentage);

		zebraUSD.mint(ZebraCore.feeReceiver(), debtFee);

		emit BorrowingFeePaid(_caller, debtFee);

		return debtFee;
	}

	function _getCollChange(uint256 _collReceived, uint256 _requestedCollWithdrawal) internal pure returns (uint256 collChange, bool isCollIncrease) {
		if (_collReceived != 0) {
			collChange = _collReceived;
			isCollIncrease = true;
		} else {
			collChange = _requestedCollWithdrawal;
		}
	}

	function _requireValidAdjustmentInCurrentMode(uint256 totalPricedCollateral, uint256 totalDebt, bool _isRecoveryMode, uint256 _collWithdrawal, bool _isDebtIncrease, LocalVariables_adjustTrove memory _vars) internal pure {
		/*
		 *In Recovery Mode, only allow:
		 *
		 * - Pure collateral top-up
		 * - Pure debt repayment
		 * - Collateral top-up with debt repayment
		 * - A debt increase combined with a collateral top-up which makes the ICR >= 150% and improves the ICR (and by extension improves the TCR).
		 *
		 * In Normal Mode, ensure:
		 *
		 * - The new ICR is above MCR
		 * - The adjustment won't pull the TCR below CCR
		 */

		// Get the trove's old ICR before the adjustment
		uint256 oldICR = ZebraMath._computeCR(_vars.coll, _vars.debt, _vars.price);

		// Get the trove's new ICR after the adjustment
		uint256 newICR = _getNewICRFromTroveChange(_vars.coll, _vars.debt, _vars.collChange, _vars.isCollIncrease, _vars.netDebtChange, _isDebtIncrease, _vars.price);

		if (_isRecoveryMode) {
			require(_collWithdrawal == 0, "BorrowerOps: Collateral withdrawal not permitted Recovery Mode");
			if (_isDebtIncrease) {
				_requireICRisAboveCCR(newICR);
				_requireNewICRisAboveOldICR(newICR, oldICR);
			}
		} else {
			// if Normal Mode
			_requireICRisAboveMCR(newICR, _vars.MCR);
			uint256 newTCR = _getNewTCRFromTroveChange(totalPricedCollateral, totalDebt, _vars.collChange * _vars.price, _vars.isCollIncrease, _vars.netDebtChange, _isDebtIncrease);
			_requireNewTCRisAboveCCR(newTCR);
		}
	}

	function _requireICRisAboveMCR(uint256 _newICR, uint256 MCR) internal pure {
		require(_newICR >= MCR, "BorrowerOps: An operation that would result in ICR < MCR is not permitted");
	}

	function _requireICRisAboveCCR(uint256 _newICR) internal pure {
		require(_newICR >= CCR, "BorrowerOps: Operation must leave trove with ICR >= CCR");
	}

	function _requireNewICRisAboveOldICR(uint256 _newICR, uint256 _oldICR) internal pure {
		require(_newICR >= _oldICR, "BorrowerOps: Cannot decrease your Trove's ICR in Recovery Mode");
	}

	function _requireNewTCRisAboveCCR(uint256 _newTCR) internal pure {
		require(_newTCR >= CCR, "BorrowerOps: An operation that would result in TCR < CCR is not permitted");
	}

	function _requireAtLeastMinNetDebt(uint256 _netDebt) internal view {
		require(_netDebt >= minNetDebt, "BorrowerOps: Trove's net debt must be greater than minimum");
	}

	function _requireValidMaxFeePercentage(uint256 _maxFeePercentage) internal pure {
		require(_maxFeePercentage <= DECIMAL_PRECISION, "Max fee percentage must less than or equal to 100%");
	}

	// Compute the new collateral ratio, considering the change in coll and debt. Assumes 0 pending rewards.
	function _getNewICRFromTroveChange(uint256 _coll, uint256 _debt, uint256 _collChange, bool _isCollIncrease, uint256 _debtChange, bool _isDebtIncrease, uint256 _price) internal pure returns (uint256) {
		(uint256 newColl, uint256 newDebt) = _getNewTroveAmounts(_coll, _debt, _collChange, _isCollIncrease, _debtChange, _isDebtIncrease);

		uint256 newICR = ZebraMath._computeCR(newColl, newDebt, _price);
		return newICR;
	}

	function _getNewTroveAmounts(uint256 _coll, uint256 _debt, uint256 _collChange, bool _isCollIncrease, uint256 _debtChange, bool _isDebtIncrease) internal pure returns (uint256, uint256) {
		uint256 newColl = _coll;
		uint256 newDebt = _debt;
		newColl = _isCollIncrease ? _coll + _collChange : _coll - _collChange;
		newDebt = _isDebtIncrease ? _debt + _debtChange : _debt - _debtChange;

		return (newColl, newDebt);
	}

	function _getNewTCRFromTroveChange(uint256 totalColl, uint256 totalDebt, uint256 _collChange, bool _isCollIncrease, uint256 _debtChange, bool _isDebtIncrease) internal pure returns (uint256) {
		totalDebt = _isDebtIncrease ? totalDebt + _debtChange : totalDebt - _debtChange;
		totalColl = _isCollIncrease ? totalColl + _collChange : totalColl - _collChange;

		uint256 newTCR = ZebraMath._computeCR(totalColl, totalDebt);
		return newTCR;
	}

	function _getTCRData(SystemBalances memory balances) internal pure returns (uint256 amount, uint256 totalPricedCollateral, uint256 totalDebt) {
		uint256 loopEnd = balances.collaterals.length;
		for (uint256 i; i < loopEnd; ) {
			totalPricedCollateral += (balances.collaterals[i] * balances.prices[i]);
			totalDebt += balances.debts[i];
			unchecked {
				++i;
			}
		}
		amount = ZebraMath._computeCR(totalPricedCollateral, totalDebt);

		return (amount, totalPricedCollateral, totalDebt);
	}

	function _getCollateralAndTCRData(ITroveManager troveManager) internal returns (IERC20 collateralToken, uint256 price, uint256 totalPricedCollateral, uint256 totalDebt, bool isRecoveryMode) {
		TroveManagerData storage t = troveManagersData[troveManager];
		uint256 index;
		(collateralToken, index) = (t.collateralToken, t.index);

		require(address(collateralToken) != address(0), "Collateral not enabled");

		uint256 amount;
		SystemBalances memory balances = fetchBalances();
		(amount, totalPricedCollateral, totalDebt) = _getTCRData(balances);
		isRecoveryMode = checkRecoveryMode(amount);

		return (collateralToken, balances.prices[index], totalPricedCollateral, totalDebt, isRecoveryMode);
	}

	function getGlobalSystemBalances() external returns (uint256 totalPricedCollateral, uint256 totalDebt) {
		SystemBalances memory balances = fetchBalances();
		(, totalPricedCollateral, totalDebt) = _getTCRData(balances);
	}
}

contracts/core/CommunityIssuance.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../dependencies/ZebraOwnableUpgradeable.sol";
import "../dependencies/ZebraMath.sol";
import "../dependencies/ZebraOwnable.sol";
import "../interfaces/IEsZebra.sol";
import "../interfaces/ICommunityIssuance.sol";

contract CommunityIssuance is ICommunityIssuance, ZebraOwnableUpgradeable {
	IEsZebra public EsZebra;
	address public stabilitypool;
	uint64 public lastUpdatedTime;
	uint64 public duration;
	uint64 public rewardEndTime;
	uint256 public rewardPerSec;
	uint256 public rewardStored;

	function initialize(IZebraCore _zebraCore, address _EsZebra, address _stabilityPool) external initializer {
		__InitCore(_zebraCore);
		EsZebra = IEsZebra(_EsZebra);
		stabilitypool = _stabilityPool;
	}

	function startUp(uint128 _rewardPerSec, uint64 _duration) external onlyOwner {
		require(rewardPerSec == 0 && rewardEndTime == 0, "CommunityIssuance: Already started");
		duration = _duration;
		rewardPerSec = _rewardPerSec;
	}

	// Returns current timestamp if the rewards program has not finished yet, end time otherwise
	function lastTimeRewardApplicable() public view returns (uint64) {
		return uint64(ZebraMath._min(block.timestamp, rewardEndTime));
	}

	function setRewardPerSec(uint128 _rewardPerSec) external onlyOwner {
		require(rewardPerSec > 0, "CommunityIssuance: invalid rewardPerSec");
		rewardStored += pendingIssues();
		rewardPerSec = _rewardPerSec;
		lastUpdatedTime = lastTimeRewardApplicable();
	}

	function setRewardEndTime(uint64 _rewardEndTime) external onlyOwner {
		require(rewardEndTime > block.timestamp, "CommunityIssuance: invalid rewardEndTime");
		rewardStored += pendingIssues();
		rewardEndTime = _rewardEndTime;
		lastUpdatedTime = lastTimeRewardApplicable();
	}

	function issueEsZebra() external override returns (uint256) {
		_requireCallerIsSP();
		if (duration == 0) {
			return 0;
		}
		if (rewardEndTime == 0) {
			rewardEndTime = uint64(block.timestamp) + duration;
		}
		if (lastUpdatedTime == 0) {
			lastUpdatedTime = lastTimeRewardApplicable();
			return 0;
		}
		if (lastUpdatedTime == lastTimeRewardApplicable()) {
			return 0;
		}
		uint256 amount = pendingReward();
		if (rewardStored != 0) {
			rewardStored = 0;
		}
		lastUpdatedTime = lastTimeRewardApplicable();
		emit EsZebraIssued(amount);
		return amount;
	}

	function sendEsZebra(address to, uint256 amount) external override {
		_requireCallerIsSP();
		require(amount > 0, "CommunityIssuance: zero amount");
		EsZebra.transfer(to, amount);
		emit EsZebraSent(to, amount);
	}

	function pendingIssues() public view returns (uint256 pending) {
		if (lastUpdatedTime == 0) {
			return 0;
		}
		if (lastTimeRewardApplicable() == lastUpdatedTime) {
			return 0;
		}
		uint256 timeDiff = lastTimeRewardApplicable() - lastUpdatedTime;
		pending = (timeDiff * rewardPerSec);
	}

	function pendingReward() public view returns (uint256 pending) {
		return pendingIssues() + rewardStored;
	}

	function _requireCallerIsSP() internal view {
		require(msg.sender == stabilitypool, "CommunityIssuance: Caller is not Stability Pool");
	}
}

contracts/core/EsZebra.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "../interfaces/IEsZebra.sol";
import "../interfaces/IZebra.sol";
import "../dependencies/ZebraOwnable.sol";

contract EsZebra is ERC20Burnable, ZebraOwnable, IEsZebra {
	address internal deployer;
	IZebra public zebra;
	address public vest;

	mapping(address => bool) public senders;

	mapping(address => bool) public receivers;

	constructor(IZebraCore _zebraCore, address _zebra, address _vest) ERC20("Escrow Zebra", "esZebra") ZebraOwnable(_zebraCore) {
		zebra = IZebra(_zebra);
		vest = _vest;
		_authSender(_vest, true);
		_authReceiver(_vest, true);
	}

	function mint(address account, uint256 amount) external {
		_requireCallerIsZebra();
		_mint(account, amount);
	}

	function authAll(address[] memory _callers, bool[] memory _enables) external onlyOwner {
		_authSenders(_callers, _enables);
		_authReceivers(_callers, _enables);
	}

	function authSenders(address[] memory _senders, bool[] memory _enables) external onlyOwner {
		_authSenders(_senders, _enables);
	}

	function _authSenders(address[] memory _senders, bool[] memory _enables) internal {
		require(_senders.length == _enables.length, "EsZebra: number of senders must be equal to the number of enables");
		for (uint256 i = 0; i < _senders.length; i++) {
			_authSender(_senders[i], _enables[i]);
		}
	}

	function _authSender(address _sender, bool _enable) internal {
		senders[_sender] = _enable;
		emit SenderUpdated(_sender, _enable);
	}

	function authReceivers(address[] memory _receivers, bool[] memory _enables) external onlyOwner {
		_authReceivers(_receivers, _enables);
	}

	function _authReceivers(address[] memory _receivers, bool[] memory _enables) internal {
		require(_receivers.length == _enables.length, "EsZebra: number of receivers must be equal to the number of enables");
		for (uint256 i = 0; i < _receivers.length; i++) {
			_authReceiver(_receivers[i], _enables[i]);
		}
	}

	function _authReceiver(address _receiver, bool _enable) internal {
		receivers[_receiver] = _enable;
		emit ReceiverUpdated(_receiver, _enable);
	}

	function burnFromZebra(address account, uint256 amount) external override {
		_requireCallerIsZebra();
		_burn(account, amount);
	}

	function burn(uint256 amount) public override(ERC20Burnable, IEsZebra) {
		ERC20Burnable.burn(amount);
	}

	function burnFrom(address account, uint256 amount) public override(ERC20Burnable, IEsZebra) {
		ERC20Burnable.burnFrom(account, amount);
	}

	function sendToken(address from, uint256 amount) external override {
		_requireCallerIsReceiver();
		_transfer(from, msg.sender, amount);
	}

	function transfer(address to, uint256 amount) public override(IERC20, ERC20) returns (bool) {
		_requireCallerIsSender();
		_transfer(msg.sender, to, amount);
		return true;
	}

	function transferFrom(address from, address to, uint256 amount) public pure override(IERC20, ERC20) returns (bool) {
		revert("EsZebra: not allowed");
	}

	function _requireCallerIsSender() internal view {
		require(senders[msg.sender], "EsZebra: invalid sender");
	}

	function _requireCallerIsReceiver() internal view {
		require(receivers[msg.sender], "EsZebra: invalid receiver");
	}

	function _requireCallerIsZebra() internal view {
		require(msg.sender == address(zebra), "EsZebra: Caller is not Zebra");
	}
}

contracts/core/Factory.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../dependencies/ZebraOwnable.sol";
import "../dependencies/console.sol";
import "../interfaces/ITroveManager.sol";
import "../interfaces/IBorrowerOperations.sol";
import "../interfaces/IZebraUSD.sol";
import "../interfaces/ISortedTroves.sol";
import "../interfaces/IStabilityPool.sol";
import "../interfaces/ILiquidationManager.sol";

/**
    @title Zebra Trove Factory
    @notice Deploys cloned pairs of `TroveManager` and `SortedTroves` in order to
            add new collateral types within the system.
 */
contract Factory is ZebraOwnable {
	using Clones for address;

	// fixed single-deployment contracts
	IZebraUSD public immutable ZebraUSD;
	IStabilityPool public immutable stabilityPool;
	ILiquidationManager public immutable liquidationManager;
	IBorrowerOperations public immutable borrowerOperations;

	// implementation contracts, redeployed each time via clone proxy
	address public sortedTrovesImpl;
	address public troveManagerImpl;

	address[] public troveManagers;

	// commented values are suggested default parameters
	struct DeploymentParams {
		uint256 minuteDecayFactor; // 999037758833783000  (half life of 12 hours)
		uint256 redemptionFeeFloor; // 1e18 / 1000 * 5  (0.5%)
		uint256 maxRedemptionFee; // 1e18  (100%)
		uint256 borrowingFeeFloor; // 1e18 / 1000 * 5  (0.5%)
		uint256 maxBorrowingFee; // 1e18 / 100 * 5  (5%)
		uint256 maxDebt;
		uint256 MCR; // 12 * 1e17  (120%)
		uint32 interestRate; // 1e4 (1%)
	}

	event NewDeployment(IERC20 collateral, address priceFeed, address troveManager, address sortedTroves);

	constructor(IZebraCore _ZebraCore, IZebraUSD _ZebraUSD, IStabilityPool _stabilityPool, IBorrowerOperations _borrowerOperations, address _sortedTroves, address _troveManager, ILiquidationManager _liquidationManager) ZebraOwnable(_ZebraCore) {
		ZebraUSD = _ZebraUSD;
		stabilityPool = _stabilityPool;
		borrowerOperations = _borrowerOperations;

		sortedTrovesImpl = _sortedTroves;
		troveManagerImpl = _troveManager;
		liquidationManager = _liquidationManager;
	}

	function troveManagerCount() external view returns (uint256) {
		return troveManagers.length;
	}

	/**
        @notice Deploy new instances of `TroveManager` and `SortedTroves`, adding
                a new collateral type to the system.
        @dev * When using the default `PriceFeed`, ensure it is configured correctly
               prior to calling this function.
             * After calling this function, the owner should also call `Vault.registerReceiver`
               to enable Zebra emissions on the newly deployed `TroveManager`
        @param collateral Collateral token to use in new deployment
        @param priceFeed Custom `PriceFeed` deployment. Leave as `address(0)` to use the default.
        @param customTroveManagerImpl Custom `TroveManager` implementation to clone from.
                                      Leave as `address(0)` to use the default.
        @param customSortedTrovesImpl Custom `SortedTroves` implementation to clone from.
                                      Leave as `address(0)` to use the default.
        @param params Struct of initial parameters to be set on the new trove manager
     */
	function deployNewInstance(IERC20 collateral, address priceFeed, address customTroveManagerImpl, address customSortedTrovesImpl, DeploymentParams memory params) external onlyOwner {
		address implementation = customTroveManagerImpl == address(0) ? troveManagerImpl : customTroveManagerImpl;
		address troveManager = implementation.cloneDeterministic(bytes32(bytes20(address(collateral))));
		troveManagers.push(troveManager);
		implementation = customSortedTrovesImpl == address(0) ? sortedTrovesImpl : customSortedTrovesImpl;
		address sortedTroves = implementation.cloneDeterministic(bytes32(bytes20(troveManager)));

		ITroveManager(troveManager).setAddresses(priceFeed, sortedTroves, collateral);
		ISortedTroves(sortedTroves).setAddresses(troveManager);

		// verify that the oracle is correctly working
		ITroveManager(troveManager).fetchPrice();

		stabilityPool.enableCollateral(collateral);
		liquidationManager.enableTroveManager(troveManager);
		ZebraUSD.enableTroveManager(troveManager);
		borrowerOperations.configureCollateral(ITroveManager(troveManager), collateral);

		ITroveManager(troveManager).setParameters(params.minuteDecayFactor, params.redemptionFeeFloor, params.maxRedemptionFee, params.borrowingFeeFloor, params.maxBorrowingFee, params.maxDebt, params.MCR, params.interestRate);

		emit NewDeployment(collateral, priceFeed, troveManager, sortedTroves);
	}

	function setImplementations(address _troveManagerImpl, address _sortedTrovesImpl) external onlyOwner {
		troveManagerImpl = _troveManagerImpl;
		sortedTrovesImpl = _sortedTrovesImpl;
	}
}

contracts/core/FeeReceiver.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../dependencies/ZebraOwnableUpgradeable.sol";

contract FeeReceiver is ZebraOwnableUpgradeable {
	function initialize(IZebraCore _zebraCore) external initializer {
		__InitCore(_zebraCore);
	}

	function withdraw(address to, IERC20 coin, uint256 value) external onlyOwner {
		if (address(coin) == address(0)) {
			(bool success, ) = to.call{ value: value }("");
			require(success, "FeeReceiver: withdraw eth failed");
		} else {
			coin.transfer(to, value);
		}
	}
}

