contracts/core/InterestDebtPool.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../interfaces/IZebraUSD.sol";
import "../dependencies/ZebraMath.sol";
import "../dependencies/console.sol";

abstract contract InterestDebtPool {
	IZebraUSD public immutable ZebraUSD;
	uint64 internal constant PRECISION = 1e18;
	uint64 internal constant SECONDS_IN_YEAR = 365 days;
	uint32 internal constant MAXFP = 1e6;
	uint32 public interestRate;
	uint256 internal outstandingInterestDebt;
	uint256 public L_Interest_Debt;
	uint256 public lastInterestDebtUpdateTime;
	uint256 public lastInterestDebtError_Redistribution;

	constructor(address _ZebraUSD) {
		ZebraUSD = IZebraUSD(_ZebraUSD);
	}

	event InterestDebtDistributed(uint256 debt);

	function getOutstandingInterestDebt() public view returns (uint256) {
		return outstandingInterestDebt;
	}

	function getCurrentOutstandingInterestDebt() public view returns (uint256) {
		return outstandingInterestDebt + getPendingSystemInterestDebt();
	}

	function decreaseOutstandingInterestDebt(uint256 amount) internal {
		outstandingInterestDebt -= amount;
	}

	function getEntireSystemDebt() public view virtual returns (uint256 entireSystemDebt);

	function _distributeInterestDebt() internal returns (uint256) {
		if (lastInterestDebtUpdateTime == 0) {
			lastInterestDebtUpdateTime = block.timestamp;
			return 0;
		}
		if (lastInterestDebtUpdateTime == block.timestamp) {
			return 0;
		}
		uint256 systemDebt = getEntireSystemDebt();
		if (systemDebt == 0) {
			return 0;
		}
		uint256 feeNumerator = (systemDebt * ((block.timestamp - lastInterestDebtUpdateTime) * interestRate * PRECISION)) / SECONDS_IN_YEAR / MAXFP + lastInterestDebtError_Redistribution;
		lastInterestDebtUpdateTime = block.timestamp;
		uint256 feeRewardPerUnitDebt = feeNumerator / systemDebt;
		lastInterestDebtError_Redistribution = feeNumerator - (feeRewardPerUnitDebt * systemDebt);
		L_Interest_Debt += feeRewardPerUnitDebt;
		uint256 interest = (feeRewardPerUnitDebt * systemDebt) / PRECISION;
		outstandingInterestDebt += interest;
		ZebraUSD.mint(feeReceiver(), interest);
		emit InterestDebtDistributed(interest);
		return feeRewardPerUnitDebt;
	}

	function feeReceiver() public view virtual returns (address);

	function getPendingSystemInterestDebt() public view returns (uint256) {
		if (lastInterestDebtUpdateTime == 0 || lastInterestDebtUpdateTime == block.timestamp) {
			return 0;
		}
		uint256 systemDebt = getEntireSystemDebt();
		if (systemDebt == 0) {
			return 0;
		}
		uint256 feeNumerator = (systemDebt * ((block.timestamp - lastInterestDebtUpdateTime) * interestRate * PRECISION)) / SECONDS_IN_YEAR / MAXFP + lastInterestDebtError_Redistribution;
		uint256 feeRewardPerUnitDebt = feeNumerator / systemDebt;
		return (feeRewardPerUnitDebt * systemDebt) / PRECISION;
	}

	function getPendingInterestDebt() public view returns (uint256) {
		if (lastInterestDebtUpdateTime == 0 || lastInterestDebtUpdateTime == block.timestamp) {
			return L_Interest_Debt;
		}
		uint256 systemDebt = getEntireSystemDebt();
		if (systemDebt == 0) {
			return L_Interest_Debt;
		}
		uint256 feeNumerator = (systemDebt * (block.timestamp - lastInterestDebtUpdateTime) * interestRate * PRECISION) / SECONDS_IN_YEAR / MAXFP + lastInterestDebtError_Redistribution;
		return L_Interest_Debt + feeNumerator / systemDebt;
	}
}

contracts/core/LiquidationManager.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/IStabilityPool.sol";
import "../interfaces/ISortedTroves.sol";
import "../interfaces/IBorrowerOperations.sol";
import "../interfaces/ITroveManager.sol";
import "../dependencies/ZebraMath.sol";
import "../dependencies/ZebraBase.sol";
import "../dependencies/console.sol";

/**
    @title Zebra Liquidation Manager
    @notice Based on Liquity's `TroveManager`
            https://github.com/liquity/dev/blob/main/packages/contracts/contracts/TroveManager.sol

            This contract has a 1:n relationship with `TroveManager`, handling liquidations
            for every active collateral within the system.

            Anyone can call to liquidate an eligible trove at any time. There is no requirement
            that liquidations happen in order according to trove ICRs. There are three ways that
            a liquidation can occur:

            1. ICR <= 100
               The trove's entire debt and collateral is redistributed between remaining active troves.

            2. 100 < ICR < MCR
               The trove is liquidated using stability pool deposits. The collateral is distributed
               amongst stability pool depositors. If the stability pool's balance is insufficient to
               completely repay the trove, the remaining debt and collateral is redistributed between
               the remaining active troves.

            3. MCR <= ICR < TCR && TCR < CCR
               The trove is liquidated using stability pool deposits. Collateral equal to MCR of
               the value of the debt is distributed between stability pool depositors. The remaining
               collateral is left claimable by the trove owner.
 */
contract LiquidationManager is ZebraBase {
	IStabilityPool public immutable stabilityPool;
	IBorrowerOperations public immutable borrowerOperations;
	address public immutable factory;

	uint256 private constant _100pct = 1000000000000000000; // 1e18 == 100%

	mapping(ITroveManager => bool) internal _enabledTroveManagers;

	/*
	 * --- Variable container structs for liquidations ---
	 *
	 * These structs are used to hold, return and assign variables inside the liquidation functions,
	 * in order to avoid the error: "CompilerError: Stack too deep".
	 **/

	struct TroveManagerValues {
		uint256 price;
		uint256 MCR;
		bool sunsetting;
	}

	struct LiquidationValues {
		uint256 entireTroveDebt;
		uint256 entireTroveColl;
		uint256 interest;
		uint256 collGasCompensation;
		uint256 debtGasCompensation;
		uint256 debtToOffset;
		uint256 collToSendToSP;
		uint256 debtToRedistribute;
		uint256 collToRedistribute;
		uint256 collSurplus;
	}

	struct LiquidationTotals {
		uint256 totalCollInSequence;
		uint256 totalDebtInSequence;
		uint256 totalInterest;
		uint256 totalCollGasCompensation;
		uint256 totalDebtGasCompensation;
		uint256 totalDebtToOffset;
		uint256 totalCollToSendToSP;
		uint256 totalDebtToRedistribute;
		uint256 totalCollToRedistribute;
		uint256 totalCollSurplus;
	}

	event TroveUpdated(address indexed _borrower, uint256 _debt, uint256 _coll, uint256 _stake, TroveManagerOperation _operation);
	event TroveLiquidated(address indexed _borrower, uint256 _debt, uint256 _coll, uint256 _interest, TroveManagerOperation _operation);
	event Liquidation(uint256 _liquidatedDebt, uint256 _liquidatedColl, uint256 _collGasCompensation, uint256 _debtGasCompensation, uint256 _interest);
	event TroveUpdated(address indexed _borrower, uint256 _debt, uint256 _coll, uint256 stake, uint8 operation);
	event TroveLiquidated(address indexed _borrower, uint256 _debt, uint256 _coll, uint8 operation);

	enum TroveManagerOperation {
		applyPendingRewards,
		liquidateInNormalMode,
		liquidateInRecoveryMode,
		redeemCollateral
	}

	constructor(IStabilityPool _stabilityPoolAddress, IBorrowerOperations _borrowerOperations, address _factory, uint256 _gasCompensation) ZebraBase(_gasCompensation) {
		stabilityPool = _stabilityPoolAddress;
		borrowerOperations = _borrowerOperations;
		factory = _factory;
	}

	function enableTroveManager(ITroveManager _troveManager) external {
		require(msg.sender == factory, "Not factory");
		_enabledTroveManagers[_troveManager] = true;
	}

	// --- Trove Liquidation functions ---

	/**
        @notice Liquidate a single trove
        @dev Reverts if the trove is not active, or cannot be liquidated
        @param borrower Borrower address to liquidate
     */
	function liquidate(ITroveManager troveManager, address borrower) external {
		require(troveManager.getTroveStatus(borrower) == 1, "TroveManager: Trove does not exist or is closed");

		address[] memory borrowers = new address[](1);
		borrowers[0] = borrower;
		batchLiquidateTroves(troveManager, borrowers);
	}

	/**
        @notice Liquidate a sequence of troves
        @dev Iterates through troves starting with the lowest ICR
        @param maxTrovesToLiquidate The maximum number of troves to liquidate
        @param maxICR Maximum ICR to liquidate. Should be set to MCR if the system
                      is not in recovery mode, to minimize gas costs for this call.
     */
	function liquidateTroves(ITroveManager troveManager, uint256 maxTrovesToLiquidate, uint256 maxICR) external {
		require(_enabledTroveManagers[troveManager], "TroveManager not approved");
		IStabilityPool stabilityPoolCached = stabilityPool;
		troveManager.distributeInterestDebt();

		ISortedTroves sortedTrovesCached = ISortedTroves(troveManager.sortedTroves());

		LiquidationValues memory singleLiquidation;
		LiquidationTotals memory totals;
		TroveManagerValues memory troveManagerValues;

		uint256 trovesRemaining = maxTrovesToLiquidate;
		uint256 troveCount = troveManager.getTroveOwnersCount();
		troveManagerValues.price = troveManager.fetchPrice();
		troveManagerValues.sunsetting = troveManager.sunsetting();
		troveManagerValues.MCR = troveManager.MCR();
		uint debtInStabPool = stabilityPoolCached.getTotalZebraUSDDeposits();

		while (trovesRemaining > 0 && troveCount > 1) {
			address account = sortedTrovesCached.getLast();
			uint ICR = troveManager.getCurrentICR(account, troveManagerValues.price);
			if (ICR > maxICR) {
				// set to 0 to ensure the next if block evaluates false
				trovesRemaining = 0;
				break;
			}
			if (ICR <= _100pct) {
				singleLiquidation = _liquidateWithoutSP(troveManager, account);
				_applyLiquidationValuesToTotals(totals, singleLiquidation);
			} else if (ICR < troveManagerValues.MCR) {
				singleLiquidation = _liquidateNormalMode(troveManager, account, debtInStabPool, troveManagerValues.sunsetting);
				debtInStabPool -= singleLiquidation.debtToOffset;
				_applyLiquidationValuesToTotals(totals, singleLiquidation);
			} else break; // break if the loop reaches a Trove with ICR >= MCR
			unchecked {
				--trovesRemaining;
				--troveCount;
			}
		}
		if (trovesRemaining > 0 && !troveManagerValues.sunsetting && troveCount > 1) {
			(uint entireSystemColl, uint entireSystemDebt) = borrowerOperations.getGlobalSystemBalances();
			entireSystemColl -= totals.totalCollToSendToSP * troveManagerValues.price;
			entireSystemDebt -= totals.totalDebtToOffset;
			address nextAccount = sortedTrovesCached.getLast();
			ITroveManager _troveManager = troveManager; //stack too deep workaround
			while (trovesRemaining > 0 && troveCount > 1) {
				uint ICR = troveManager.getCurrentICR(nextAccount, troveManagerValues.price);
				if (ICR > maxICR) break;
				unchecked {
					--trovesRemaining;
				}
				address account = nextAccount;
				nextAccount = sortedTrovesCached.getPrev(account);

				uint256 TCR = ZebraMath._computeCR(entireSystemColl, entireSystemDebt);
				if (TCR >= CCR || ICR >= TCR) break;
				singleLiquidation = _tryLiquidateWithCap(_troveManager, account, debtInStabPool, troveManagerValues.MCR, troveManagerValues.price);
				if (singleLiquidation.debtToOffset == 0) continue;
				debtInStabPool -= singleLiquidation.debtToOffset;
				entireSystemColl -= (singleLiquidation.collToSendToSP + singleLiquidation.collSurplus) * troveManagerValues.price;
				entireSystemDebt -= singleLiquidation.debtToOffset;
				_applyLiquidationValuesToTotals(totals, singleLiquidation);
				unchecked {
					--troveCount;
				}
			}
		}

		require(totals.totalDebtInSequence > 0, "nothing to liquidate");
		if (totals.totalDebtToOffset > 0 || totals.totalCollToSendToSP > 0) {
			// Move liquidated collateral and Debt to the appropriate pools
			stabilityPoolCached.offset(troveManager.collateralToken(), totals.totalDebtToOffset, totals.totalCollToSendToSP);
			troveManager.decreaseDebtAndSendCollateral(address(stabilityPoolCached), totals.totalDebtToOffset, totals.totalCollToSendToSP);
		}
		troveManager.finalizeLiquidation(msg.sender, totals.totalDebtToRedistribute, totals.totalCollToRedistribute, totals.totalCollSurplus, totals.totalDebtGasCompensation, totals.totalCollGasCompensation, totals.totalInterest);

		emit Liquidation(totals.totalDebtInSequence, totals.totalCollInSequence - totals.totalCollGasCompensation - totals.totalCollSurplus, totals.totalCollGasCompensation, totals.totalDebtGasCompensation, totals.totalInterest);
	}

	/**
        @notice Liquidate a custom list of troves
        @dev Reverts if there is not a single trove that can be liquidated
        @param _troveArray List of borrower addresses to liquidate. Troves that were already
                           liquidated, or cannot be liquidated, are ignored.
     */
	/*
	 * Attempt to liquidate a custom list of troves provided by the caller.
	 */
	function batchLiquidateTroves(ITroveManager troveManager, address[] memory _troveArray) public {
		require(_enabledTroveManagers[troveManager], "TroveManager not approved");
		require(_troveArray.length != 0, "TroveManager: Calldata address array must not be empty");
		troveManager.distributeInterestDebt();

		LiquidationValues memory singleLiquidation;
		LiquidationTotals memory totals;
		TroveManagerValues memory troveManagerValues;

		IStabilityPool stabilityPoolCached = stabilityPool;
		uint debtInStabPool = stabilityPoolCached.getTotalZebraUSDDeposits();
		troveManagerValues.price = troveManager.fetchPrice();
		troveManagerValues.sunsetting = troveManager.sunsetting();
		troveManagerValues.MCR = troveManager.MCR();
		uint troveCount = troveManager.getTroveOwnersCount();
		uint length = _troveArray.length;
		uint troveIter;
		while (troveIter < length && troveCount > 1) {
			// first iteration round, when all liquidated troves have ICR < MCR we do not need to track TCR
			address account = _troveArray[troveIter];

			// closed / non-existent troves return an ICR of type(uint).max and are ignored
			uint ICR = troveManager.getCurrentICR(account, troveManagerValues.price);
			if (ICR <= _100pct) {
				singleLiquidation = _liquidateWithoutSP(troveManager, account);
			} else if (ICR < troveManagerValues.MCR) {
				singleLiquidation = _liquidateNormalMode(troveManager, account, debtInStabPool, troveManagerValues.sunsetting);
				debtInStabPool -= singleLiquidation.debtToOffset;
			} else {
				// As soon as we find a trove with ICR >= MCR we need to start tracking the global TCR with the next loop
				break;
			}
			_applyLiquidationValuesToTotals(totals, singleLiquidation);
			unchecked {
				++troveIter;
				--troveCount;
			}
		}

		if (troveIter < length && troveCount > 1) {
			// second iteration round, if we receive a trove with ICR > MCR and need to track TCR
			(uint256 entireSystemColl, uint256 entireSystemDebt) = borrowerOperations.getGlobalSystemBalances();
			entireSystemColl -= totals.totalCollToSendToSP * troveManagerValues.price;
			entireSystemDebt -= totals.totalDebtToOffset;
			while (troveIter < length && troveCount > 1) {
				address account = _troveArray[troveIter];
				uint ICR = troveManager.getCurrentICR(account, troveManagerValues.price);
				unchecked {
					++troveIter;
				}
				if (ICR <= _100pct) {
					singleLiquidation = _liquidateWithoutSP(troveManager, account);
				} else if (ICR < troveManagerValues.MCR) {
					singleLiquidation = _liquidateNormalMode(troveManager, account, debtInStabPool, troveManagerValues.sunsetting);
				} else {
					uint256 TCR = ZebraMath._computeCR(entireSystemColl, entireSystemDebt);
					if (TCR >= CCR || ICR >= TCR || troveManagerValues.sunsetting) continue;
					singleLiquidation = _tryLiquidateWithCap(troveManager, account, debtInStabPool, troveManagerValues.MCR, troveManagerValues.price);
					if (singleLiquidation.debtToOffset == 0) continue;
				}

				debtInStabPool -= singleLiquidation.debtToOffset;
				entireSystemColl -= (singleLiquidation.collToSendToSP + singleLiquidation.collSurplus) * troveManagerValues.price;
				entireSystemDebt -= singleLiquidation.debtToOffset;
				_applyLiquidationValuesToTotals(totals, singleLiquidation);
				unchecked {
					--troveCount;
				}
			}
		}

		require(totals.totalDebtInSequence > 0, "TroveManager: nothing to liquidate");

		if (totals.totalDebtToOffset > 0 || totals.totalCollToSendToSP > 0) {
			// Move liquidated collateral and Debt to the appropriate pools
			stabilityPoolCached.offset(troveManager.collateralToken(), totals.totalDebtToOffset, totals.totalCollToSendToSP);
			troveManager.decreaseDebtAndSendCollateral(address(stabilityPoolCached), totals.totalDebtToOffset, totals.totalCollToSendToSP);
		}
		troveManager.finalizeLiquidation(msg.sender, totals.totalDebtToRedistribute, totals.totalCollToRedistribute, totals.totalCollSurplus, totals.totalDebtGasCompensation, totals.totalCollGasCompensation, totals.totalInterest);

		emit Liquidation(totals.totalDebtInSequence, totals.totalCollInSequence - totals.totalCollGasCompensation - totals.totalCollSurplus, totals.totalCollGasCompensation, totals.totalDebtGasCompensation, totals.totalInterest);
	}

	/**
        @dev Perform a "normal" liquidation, where 100% < ICR < MCR. The trove
             is liquidated as much as possible using the stability pool. Any
             remaining debt and collateral are redistributed between active troves.
     */
	function _liquidateNormalMode(ITroveManager troveManager, address _borrower, uint256 _debtInStabPool, bool sunsetting) internal returns (LiquidationValues memory singleLiquidation) {
		uint pendingDebtReward;
		uint pendingCollReward;

		(singleLiquidation.entireTroveDebt, singleLiquidation.entireTroveColl, pendingDebtReward, pendingCollReward) = troveManager.getEntireDebtAndColl(_borrower);
		singleLiquidation.interest = troveManager.getTroveInterest(_borrower, singleLiquidation.entireTroveDebt);
		singleLiquidation.entireTroveDebt = singleLiquidation.entireTroveDebt + singleLiquidation.interest;
		troveManager.movePendingTroveRewardsToActiveBalances(pendingDebtReward, pendingCollReward);

		singleLiquidation.collGasCompensation = _getCollGasCompensation(singleLiquidation.entireTroveColl);
		singleLiquidation.debtGasCompensation = DEBT_GAS_COMPENSATION;
		uint256 collToLiquidate = singleLiquidation.entireTroveColl - singleLiquidation.collGasCompensation;

		(singleLiquidation.debtToOffset, singleLiquidation.collToSendToSP, singleLiquidation.debtToRedistribute, singleLiquidation.collToRedistribute) = _getOffsetAndRedistributionVals(singleLiquidation.entireTroveDebt, collToLiquidate, _debtInStabPool, sunsetting);

		troveManager.closeTroveByLiquidation(_borrower);
		emit TroveLiquidated(_borrower, singleLiquidation.entireTroveDebt, singleLiquidation.entireTroveColl, singleLiquidation.interest, TroveManagerOperation.liquidateInNormalMode);
		emit TroveUpdated(_borrower, 0, 0, 0, TroveManagerOperation.liquidateInNormalMode);
		return singleLiquidation;
	}

	/**
        @dev Attempt to liquidate a single trove in recovery mode.
             If MCR <= ICR < current TCR (accounting for the preceding liquidations in the current sequence)
             and there is Debt in the Stability Pool, only offset, with no redistribution,
             but at a capped rate of 1.1 and only if the whole debt can be liquidated.
             The remainder due to the capped rate will be claimable as collateral surplus.
     */
	function _tryLiquidateWithCap(ITroveManager troveManager, address _borrower, uint256 _debtInStabPool, uint256 _MCR, uint256 _price) internal returns (LiquidationValues memory singleLiquidation) {
		uint entireTroveDebt;
		uint entireTroveColl;
		uint pendingDebtReward;
		uint pendingCollReward;

		(entireTroveDebt, entireTroveColl, pendingDebtReward, pendingCollReward) = troveManager.getEntireDebtAndColl(_borrower);
		uint256 interest = troveManager.getTroveInterest(_borrower, entireTroveDebt);
		entireTroveDebt = entireTroveDebt + interest;
		if (entireTroveDebt > _debtInStabPool) {
			// do not liquidate if the entire trove cannot be liquidated via SP
			return singleLiquidation;
		}

		troveManager.movePendingTroveRewardsToActiveBalances(pendingDebtReward, pendingCollReward);

		singleLiquidation.entireTroveDebt = entireTroveDebt + interest;
		singleLiquidation.interest = interest;
		singleLiquidation.entireTroveColl = entireTroveColl;
		uint256 collToOffset = (singleLiquidation.entireTroveDebt * _MCR) / _price;

		singleLiquidation.collGasCompensation = _getCollGasCompensation(collToOffset);
		singleLiquidation.debtGasCompensation = DEBT_GAS_COMPENSATION;

		singleLiquidation.debtToOffset = singleLiquidation.entireTroveDebt;
		singleLiquidation.collToSendToSP = collToOffset - singleLiquidation.collGasCompensation;

		troveManager.closeTroveByLiquidation(_borrower);

		uint256 collSurplus = entireTroveColl - collToOffset;
		if (collSurplus > 0) {
			singleLiquidation.collSurplus = collSurplus;
			troveManager.addCollateralSurplus(_borrower, collSurplus);
		}

		emit TroveLiquidated(_borrower, entireTroveDebt, singleLiquidation.collToSendToSP, interest, TroveManagerOperation.liquidateInRecoveryMode);
		emit TroveUpdated(_borrower, 0, 0, 0, TroveManagerOperation.liquidateInRecoveryMode);

		return singleLiquidation;
	}

	/**
        @dev Liquidate a trove without using the stability pool. All debt and collateral
             are distributed porportionally between the remaining active troves.
     */
	function _liquidateWithoutSP(ITroveManager troveManager, address _borrower) internal returns (LiquidationValues memory singleLiquidation) {
		uint pendingDebtReward;
		uint pendingCollReward;

		(singleLiquidation.entireTroveDebt, singleLiquidation.entireTroveColl, pendingDebtReward, pendingCollReward) = troveManager.getEntireDebtAndColl(_borrower);
		singleLiquidation.interest = troveManager.getTroveInterest(_borrower, singleLiquidation.entireTroveDebt);
		singleLiquidation.entireTroveDebt = singleLiquidation.entireTroveDebt + singleLiquidation.interest;
		singleLiquidation.collGasCompensation = _getCollGasCompensation(singleLiquidation.entireTroveColl);
		singleLiquidation.debtGasCompensation = DEBT_GAS_COMPENSATION;
		troveManager.movePendingTroveRewardsToActiveBalances(pendingDebtReward, pendingCollReward);

		singleLiquidation.debtToOffset = 0;
		singleLiquidation.collToSendToSP = 0;
		singleLiquidation.debtToRedistribute = singleLiquidation.entireTroveDebt;
		singleLiquidation.collToRedistribute = singleLiquidation.entireTroveColl - singleLiquidation.collGasCompensation;

		troveManager.closeTroveByLiquidation(_borrower);
		emit TroveLiquidated(_borrower, singleLiquidation.entireTroveDebt, singleLiquidation.entireTroveColl, singleLiquidation.interest, TroveManagerOperation.liquidateInRecoveryMode);
		emit TroveUpdated(_borrower, 0, 0, 0, TroveManagerOperation.liquidateInRecoveryMode);
		return singleLiquidation;
	}

	/* In a full liquidation, returns the values for a trove's coll and debt to be offset, and coll and debt to be
	 * redistributed to active troves.
	 */
	function _getOffsetAndRedistributionVals(uint256 _debt, uint256 _coll, uint256 _debtInStabPool, bool sunsetting) internal pure returns (uint256 debtToOffset, uint256 collToSendToSP, uint256 debtToRedistribute, uint256 collToRedistribute) {
		if (_debtInStabPool > 0 && !sunsetting) {
			/*
			 * Offset as much debt & collateral as possible against the Stability Pool, and redistribute the remainder
			 * between all active troves.
			 *
			 *  If the trove's debt is larger than the deposited Debt in the Stability Pool:
			 *
			 *  - Offset an amount of the trove's debt equal to the Debt in the Stability Pool
			 *  - Send a fraction of the trove's collateral to the Stability Pool, equal to the fraction of its offset debt
			 *
			 */
			debtToOffset = ZebraMath._min(_debt, _debtInStabPool);
			collToSendToSP = (_coll * debtToOffset) / _debt;
			debtToRedistribute = _debt - debtToOffset;
			collToRedistribute = _coll - collToSendToSP;
		} else {
			debtToOffset = 0;
			collToSendToSP = 0;
			debtToRedistribute = _debt;
			collToRedistribute = _coll;
		}
	}

	/**
        @dev Adds values from `singleLiquidation` to `totals`
             Calling this function mutates `totals`, the change is done in-place
             to avoid needless expansion of memory
     */
	function _applyLiquidationValuesToTotals(LiquidationTotals memory totals, LiquidationValues memory singleLiquidation) internal pure {
		// Tally all the values with their respective running totals
		totals.totalCollGasCompensation = totals.totalCollGasCompensation + singleLiquidation.collGasCompensation;
		totals.totalDebtGasCompensation = totals.totalDebtGasCompensation + singleLiquidation.debtGasCompensation;
		totals.totalDebtInSequence = totals.totalDebtInSequence + singleLiquidation.entireTroveDebt;
		totals.totalInterest = totals.totalInterest + singleLiquidation.interest;
		totals.totalCollInSequence = totals.totalCollInSequence + singleLiquidation.entireTroveColl;
		totals.totalDebtToOffset = totals.totalDebtToOffset + singleLiquidation.debtToOffset;
		totals.totalCollToSendToSP = totals.totalCollToSendToSP + singleLiquidation.collToSendToSP;
		totals.totalDebtToRedistribute = totals.totalDebtToRedistribute + singleLiquidation.debtToRedistribute;
		totals.totalCollToRedistribute = totals.totalCollToRedistribute + singleLiquidation.collToRedistribute;
		totals.totalCollSurplus = totals.totalCollSurplus + singleLiquidation.collSurplus;
	}
}

contracts/core/PriceFeed.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts/utils/Address.sol";
import "../interfaces/IPyth.sol";
import "../dependencies/ZebraMath.sol";
import "../dependencies/ZebraOwnable.sol";
import "../dependencies/console.sol";

/**
    @title Zebra Multi Token Price Feed
    @notice Based on Gravita's PriceFeed:
            https://github.com/Gravita-Protocol/Gravita-SmartContracts/blob/9b69d555f3567622b0f84df8c7f1bb5cd9323573/contracts/PriceFeed.sol

            Zebra's implementation additionally caches price values within a block and incorporates exchange rate settings for derivative tokens (e.g. stETH -> wstETH).
 */
contract PriceFeed is ZebraOwnable {
	struct OracleRecord {
		IPyth pyth;
		uint32 decimals;
		uint32 heartbeat;
		bool isFeedWorking;
	}

	struct PriceRecord {
		uint96 scaledPrice;
		uint32 timestamp;
		uint32 lastUpdated;
	}

	struct FeedResponse {
		int64 price;
		// Confidence interval around the price
		uint64 conf;
		// Price exponent
		int32 expo;
		// Unix timestamp describing when the price was published
		uint publishTime;
		bool success;
	}

	// Custom Errors --------------------------------------------------------------------------------------------------

	error PriceFeed__InvalidFeedResponseError(address token);
	error PriceFeed__FeedFrozenError(address token);
	error PriceFeed__UnknownFeedError(address token);
	error PriceFeed__HeartbeatOutOfBoundsError();

	// Events ---------------------------------------------------------------------------------------------------------

	event NewOracleRegistered(address token, address pyth);
	event PriceFeedStatusUpdated(address token, address oracle, bool isWorking);
	event PriceRecordUpdated(address indexed token, uint256 _price);

	/** Constants ---------------------------------------------------------------------------------------------------- */

	// Used to convert a chainlink price answer to an 18-digit precision uint
	uint256 public constant TARGET_DIGITS = 18;

	// Responses are considered stale this many seconds after the oracle's heartbeat
	uint256 public constant RESPONSE_TIMEOUT_BUFFER = 1 hours;

	// Maximum deviation allowed between two consecutive Chainlink oracle prices. 18-digit precision.
	uint256 public constant MAX_PRICE_DEVIATION_FROM_PREVIOUS_ROUND = 5e17; // 50%
	bytes32 public constant ZetaUSDFEED = 0xb70656181007f487e392bf0d92e55358e9f0da5da6531c7c4ce7828aa11277fe;
	// State ------------------------------------------------------------------------------------------------------------

	mapping(address => OracleRecord) public oracleRecords;
	mapping(address => PriceRecord) public priceRecords;

	constructor(IZebraCore _ZebraCore) ZebraOwnable(_ZebraCore) {}

	// Admin routines ---------------------------------------------------------------------------------------------------

	/**
        @notice Set the oracle for a specific token
        @param _token Address of the LST to set the oracle for
        @param _pyth Address of the pyth oracle for this LST
        @param _heartbeat Oracle heartbeat, in seconds
     */
	function setOracle(address _token, address _pyth, uint32 _heartbeat) public onlyOwner {
		if (_heartbeat > 86400) revert PriceFeed__HeartbeatOutOfBoundsError();
		IPyth newFeed = IPyth(_pyth);
		FeedResponse memory currResponse = _fetchFeedResponses(newFeed);
		if (!_isFeedWorking(currResponse)) {
			revert PriceFeed__InvalidFeedResponseError(_token);
		}
		if (_isPriceStale(currResponse.publishTime, _heartbeat)) {
			revert PriceFeed__FeedFrozenError(_token);
		}

		OracleRecord memory record = OracleRecord({ pyth: newFeed, decimals: uint32(-currResponse.expo), heartbeat: _heartbeat, isFeedWorking: true });

		oracleRecords[_token] = record;
		PriceRecord memory _priceRecord = priceRecords[_token];

		_processFeedResponses(_token, record, currResponse, _priceRecord);
		emit NewOracleRegistered(_token, _pyth);
	}

	// Public functions -------------------------------------------------------------------------------------------------

	/**
        @notice Get the latest price returned from the oracle
        @dev You can obtain these values by calling `TroveManager.fetchPrice()`
             rather than directly interacting with this contract.
        @param _token Token to fetch the price for
        @return The latest valid price for the requested token
     */
	function fetchPrice(address _token) public returns (uint256) {
		PriceRecord memory priceRecord = priceRecords[_token];
		if (priceRecord.lastUpdated == block.timestamp) {
			// We short-circuit only if the price was already correct in the current block
			return priceRecord.scaledPrice;
		}
		if (priceRecord.lastUpdated == 0) {
			revert PriceFeed__UnknownFeedError(_token);
		}

		OracleRecord storage oracle = oracleRecords[_token];

		FeedResponse memory currResponse = _fetchFeedResponses(oracle.pyth);
		if (_isPriceStale(priceRecord.timestamp, oracle.heartbeat)) {
			revert PriceFeed__FeedFrozenError(_token);
		}

		return _processFeedResponses(_token, oracle, currResponse, priceRecord);
	}

	// Internal functions -----------------------------------------------------------------------------------------------

	function _processFeedResponses(address _token, OracleRecord memory oracle, FeedResponse memory _currResponse, PriceRecord memory priceRecord) internal returns (uint256) {
		uint32 decimals = oracle.decimals;
		bool isValidResponse = _isFeedWorking(_currResponse) && !_isPriceStale(_currResponse.publishTime, oracle.heartbeat);
		if (isValidResponse) {
			uint256 scaledPrice = _scalePriceByDigits(_currResponse.price, decimals);
			if (!oracle.isFeedWorking) {
				_updateFeedStatus(_token, oracle, true);
			}
			_storePrice(_token, scaledPrice, _currResponse.publishTime);
			return scaledPrice;
		} else {
			if (oracle.isFeedWorking) {
				_updateFeedStatus(_token, oracle, false);
			}
			if (_isPriceStale(priceRecord.timestamp, oracle.heartbeat)) {
				revert PriceFeed__FeedFrozenError(_token);
			}
			return priceRecord.scaledPrice;
		}
	}

	function _calcEthPrice(uint256 ethAmount) internal returns (uint256) {
		uint256 ethPrice = fetchPrice(address(0));
		return (ethPrice * ethAmount) / 1 ether;
	}

	function _fetchFeedResponses(IPyth oracle) internal view returns (FeedResponse memory currResponse) {
		currResponse = _fetchCurrentFeedResponse(oracle);
	}

	function _isPriceStale(uint256 _priceTimestamp, uint256 _heartbeat) internal view returns (bool) {
		return block.timestamp - _priceTimestamp > _heartbeat + RESPONSE_TIMEOUT_BUFFER;
	}

	function _isFeedWorking(FeedResponse memory _currentResponse) internal view returns (bool) {
		return _isValidResponse(_currentResponse);
	}

	function _isValidResponse(FeedResponse memory _response) internal view returns (bool) {
		return (_response.success) && (_response.publishTime != 0) && (_response.publishTime <= block.timestamp) && (_response.price != 0);
	}

	function _scalePriceByDigits(int64 _price, uint256 _answerDigits) internal pure returns (uint256) {
		if (_answerDigits == TARGET_DIGITS) {
			return uint256(uint64(_price));
		} else if (_answerDigits < TARGET_DIGITS) {
			// Scale the returned price value up to target precision
			return uint256(uint64(_price)) * (10 ** (TARGET_DIGITS - _answerDigits));
		} else {
			// Scale the returned price value down to target precision
			return uint256(uint64(_price)) / (10 ** (_answerDigits - TARGET_DIGITS));
		}
	}

	function _updateFeedStatus(address _token, OracleRecord memory _oracle, bool _isWorking) internal {
		oracleRecords[_token].isFeedWorking = _isWorking;
		emit PriceFeedStatusUpdated(_token, address(_oracle.pyth), _isWorking);
	}

	function _storePrice(address _token, uint256 _price, uint256 _timestamp) internal {
		priceRecords[_token] = PriceRecord({ scaledPrice: uint96(_price), timestamp: uint32(_timestamp), lastUpdated: uint32(block.timestamp) });
		emit PriceRecordUpdated(_token, _price);
	}

	function _fetchCurrentFeedResponse(IPyth _priceAggregator) internal view returns (FeedResponse memory response) {
		try _priceAggregator.getPriceUnsafe(ZetaUSDFEED) returns (IPyth.Price memory price) {
			response.price = price.price;
			response.conf = price.conf;
			response.expo = price.expo;
			response.publishTime = price.publishTime;
			response.success = true;
		} catch {
			// If call to Chainlink aggregator reverts, return a zero response with success = false
			return response;
		}
	}
}

contracts/core/SortedTroves.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../interfaces/ITroveManager.sol";

/**
    @title Zebra Sorted Troves
    @notice Based on Liquity's `SortedTroves`:
            https://github.com/liquity/dev/blob/main/packages/contracts/contracts/SortedTroves.sol

            Originally derived from `SortedDoublyLinkedList`:
            https://github.com/livepeer/protocol/blob/master/contracts/libraries/SortedDoublyLL.sol
 */
contract SortedTroves {
	ITroveManager public troveManager;

	Data public data;

	// Information for a node in the list
	struct Node {
		bool exists;
		address nextId; // Id of next node (smaller NICR) in the list
		address prevId; // Id of previous node (larger NICR) in the list
	}

	// Information for the list
	struct Data {
		address head; // Head of the list. Also the node in the list with the largest NICR
		address tail; // Tail of the list. Also the node in the list with the smallest NICR
		uint256 size; // Current size of the list
		mapping(address => Node) nodes; // Track the corresponding ids for each node in the list
	}

	event NodeAdded(address _id, uint256 _NICR);
	event NodeRemoved(address _id);

	function setAddresses(address _troveManagerAddress) external {
		require(address(troveManager) == address(0), "Already set");
		troveManager = ITroveManager(_troveManagerAddress);
	}

	/*
	 * @dev Add a node to the list
	 * @param _id Node's id
	 * @param _NICR Node's NICR
	 * @param _prevId Id of previous node for the insert position
	 * @param _nextId Id of next node for the insert position
	 */

	function insert(address _id, uint256 _NICR, address _prevId, address _nextId) external {
		ITroveManager troveManagerCached = troveManager;

		_requireCallerIsTroveManager(troveManagerCached);

		Node storage node = data.nodes[_id];
		// List must not already contain node
		require(!node.exists, "SortedTroves: List already contains the node");
		// Node id must not be null
		require(_id != address(0), "SortedTroves: Id cannot be zero");

		_insert(node, troveManagerCached, _id, _NICR, _prevId, _nextId);
	}

	function _insert(Node storage node, ITroveManager _troveManager, address _id, uint256 _NICR, address _prevId, address _nextId) internal {
		// NICR must be non-zero
		require(_NICR > 0, "SortedTroves: NICR must be positive");

		address prevId = _prevId;
		address nextId = _nextId;

		if (!_validInsertPosition(_troveManager, _NICR, prevId, nextId)) {
			// Sender's hint was not a valid insert position
			// Use sender's hint to find a valid insert position
			(prevId, nextId) = _findInsertPosition(_troveManager, _NICR, prevId, nextId);
		}

		node.exists = true;

		if (prevId == address(0) && nextId == address(0)) {
			// Insert as head and tail
			data.head = _id;
			data.tail = _id;
		} else if (prevId == address(0)) {
			// Insert before `prevId` as the head
			address head = data.head;
			node.nextId = head;
			data.nodes[head].prevId = _id;
			data.head = _id;
		} else if (nextId == address(0)) {
			// Insert after `nextId` as the tail
			address tail = data.tail;
			node.prevId = tail;
			data.nodes[tail].nextId = _id;
			data.tail = _id;
		} else {
			// Insert at insert position between `prevId` and `nextId`
			node.nextId = nextId;
			node.prevId = prevId;
			data.nodes[prevId].nextId = _id;
			data.nodes[nextId].prevId = _id;
		}

		data.size = data.size + 1;
		emit NodeAdded(_id, _NICR);
	}

	function remove(address _id) external {
		_requireCallerIsTroveManager(troveManager);
		_remove(data.nodes[_id], _id);
	}

	/*
	 * @dev Remove a node from the list
	 * @param _id Node's id
	 */
	function _remove(Node storage node, address _id) internal {
		// List must contain the node
		require(node.exists, "SortedTroves: List does not contain the id");

		if (data.size > 1) {
			// List contains more than a single node
			if (_id == data.head) {
				// The removed node is the head
				// Set head to next node
				address head = node.nextId;
				data.head = head;
				// Set prev pointer of new head to null
				data.nodes[head].prevId = address(0);
			} else if (_id == data.tail) {
				address tail = node.prevId;
				// The removed node is the tail
				// Set tail to previous node
				data.tail = tail;
				// Set next pointer of new tail to null
				data.nodes[tail].nextId = address(0);
			} else {
				address prevId = node.prevId;
				address nextId = node.nextId;
				// The removed node is neither the head nor the tail
				// Set next pointer of previous node to the next node
				data.nodes[prevId].nextId = nextId;
				// Set prev pointer of next node to the previous node
				data.nodes[nextId].prevId = prevId;
			}
		} else {
			// List contains a single node
			// Set the head and tail to null
			data.head = address(0);
			data.tail = address(0);
		}

		delete data.nodes[_id];
		data.size = data.size - 1;
		emit NodeRemoved(_id);
	}

	/*
	 * @dev Re-insert the node at a new position, based on its new NICR
	 * @param _id Node's id
	 * @param _newNICR Node's new NICR
	 * @param _prevId Id of previous node for the new insert position
	 * @param _nextId Id of next node for the new insert position
	 */
	function reInsert(address _id, uint256 _newNICR, address _prevId, address _nextId) external {
		ITroveManager troveManagerCached = troveManager;

		_requireCallerIsTroveManager(troveManagerCached);

		Node storage node = data.nodes[_id];

		// Remove node from the list
		_remove(node, _id);

		_insert(node, troveManagerCached, _id, _newNICR, _prevId, _nextId);
	}

	/*
	 * @dev Checks if the list contains a node
	 */
	function contains(address _id) public view returns (bool) {
		return data.nodes[_id].exists;
	}

	/*
	 * @dev Checks if the list is empty
	 */
	function isEmpty() public view returns (bool) {
		return data.size == 0;
	}

	/*
	 * @dev Returns the current size of the list
	 */
	function getSize() external view returns (uint256) {
		return data.size;
	}

	/*
	 * @dev Returns the first node in the list (node with the largest NICR)
	 */
	function getFirst() external view returns (address) {
		return data.head;
	}

	/*
	 * @dev Returns the last node in the list (node with the smallest NICR)
	 */
	function getLast() external view returns (address) {
		return data.tail;
	}

	/*
	 * @dev Returns the next node (with a smaller NICR) in the list for a given node
	 * @param _id Node's id
	 */
	function getNext(address _id) external view returns (address) {
		return data.nodes[_id].nextId;
	}

	/*
	 * @dev Returns the previous node (with a larger NICR) in the list for a given node
	 * @param _id Node's id
	 */
	function getPrev(address _id) external view returns (address) {
		return data.nodes[_id].prevId;
	}

	/*
	 * @dev Check if a pair of nodes is a valid insertion point for a new node with the given NICR
	 * @param _NICR Node's NICR
	 * @param _prevId Id of previous node for the insert position
	 * @param _nextId Id of next node for the insert position
	 */
	function validInsertPosition(uint256 _NICR, address _prevId, address _nextId) external view returns (bool) {
		return _validInsertPosition(troveManager, _NICR, _prevId, _nextId);
	}

	function _validInsertPosition(ITroveManager _troveManager, uint256 _NICR, address _prevId, address _nextId) internal view returns (bool) {
		if (_prevId == address(0) && _nextId == address(0)) {
			// `(null, null)` is a valid insert position if the list is empty
			return isEmpty();
		} else if (_prevId == address(0)) {
			// `(null, _nextId)` is a valid insert position if `_nextId` is the head of the list
			return data.head == _nextId && _NICR >= _troveManager.getNominalICR(_nextId);
		} else if (_nextId == address(0)) {
			// `(_prevId, null)` is a valid insert position if `_prevId` is the tail of the list
			return data.tail == _prevId && _NICR <= _troveManager.getNominalICR(_prevId);
		} else {
			// `(_prevId, _nextId)` is a valid insert position if they are adjacent nodes and `_NICR` falls between the two nodes' NICRs
			return data.nodes[_prevId].nextId == _nextId && _troveManager.getNominalICR(_prevId) >= _NICR && _NICR >= _troveManager.getNominalICR(_nextId);
		}
	}

	/*
	 * @dev Descend the list (larger NICRs to smaller NICRs) to find a valid insert position
	 * @param _troveManager TroveManager contract, passed in as param to save SLOADs
	 * @param _NICR Node's NICR
	 * @param _startId Id of node to start descending the list from
	 */
	function _descendList(ITroveManager _troveManager, uint256 _NICR, address _startId) internal view returns (address, address) {
		// If `_startId` is the head, check if the insert position is before the head
		if (data.head == _startId && _NICR >= _troveManager.getNominalICR(_startId)) {
			return (address(0), _startId);
		}

		address prevId = _startId;
		address nextId = data.nodes[prevId].nextId;

		// Descend the list until we reach the end or until we find a valid insert position
		while (prevId != address(0) && !_validInsertPosition(_troveManager, _NICR, prevId, nextId)) {
			prevId = data.nodes[prevId].nextId;
			nextId = data.nodes[prevId].nextId;
		}

		return (prevId, nextId);
	}

	/*
	 * @dev Ascend the list (smaller NICRs to larger NICRs) to find a valid insert position
	 * @param _troveManager TroveManager contract, passed in as param to save SLOADs
	 * @param _NICR Node's NICR
	 * @param _startId Id of node to start ascending the list from
	 */
	function _ascendList(ITroveManager _troveManager, uint256 _NICR, address _startId) internal view returns (address, address) {
		// If `_startId` is the tail, check if the insert position is after the tail
		if (data.tail == _startId && _NICR <= _troveManager.getNominalICR(_startId)) {
			return (_startId, address(0));
		}

		address nextId = _startId;
		address prevId = data.nodes[nextId].prevId;

		// Ascend the list until we reach the end or until we find a valid insertion point
		while (nextId != address(0) && !_validInsertPosition(_troveManager, _NICR, prevId, nextId)) {
			nextId = data.nodes[nextId].prevId;
			prevId = data.nodes[nextId].prevId;
		}

		return (prevId, nextId);
	}

	/*
	 * @dev Find the insert position for a new node with the given NICR
	 * @param _NICR Node's NICR
	 * @param _prevId Id of previous node for the insert position
	 * @param _nextId Id of next node for the insert position
	 */
	function findInsertPosition(uint256 _NICR, address _prevId, address _nextId) external view returns (address, address) {
		return _findInsertPosition(troveManager, _NICR, _prevId, _nextId);
	}

	function _findInsertPosition(ITroveManager _troveManager, uint256 _NICR, address _prevId, address _nextId) internal view returns (address, address) {
		address prevId = _prevId;
		address nextId = _nextId;

		if (prevId != address(0)) {
			if (!contains(prevId) || _NICR > _troveManager.getNominalICR(prevId)) {
				// `prevId` does not exist anymore or now has a smaller NICR than the given NICR
				prevId = address(0);
			}
		}

		if (nextId != address(0)) {
			if (!contains(nextId) || _NICR < _troveManager.getNominalICR(nextId)) {
				// `nextId` does not exist anymore or now has a larger NICR than the given NICR
				nextId = address(0);
			}
		}

		if (prevId == address(0) && nextId == address(0)) {
			// No hint - descend list starting from head
			return _descendList(_troveManager, _NICR, data.head);
		} else if (prevId == address(0)) {
			// No `prevId` for hint - ascend list starting from `nextId`
			return _ascendList(_troveManager, _NICR, nextId);
		} else if (nextId == address(0)) {
			// No `nextId` for hint - descend list starting from `prevId`
			return _descendList(_troveManager, _NICR, prevId);
		} else {
			// Descend list starting from `prevId`
			return _descendList(_troveManager, _NICR, prevId);
		}
	}

	function _requireCallerIsTroveManager(ITroveManager _troveManager) internal view {
		require(msg.sender == address(_troveManager), "SortedTroves: Caller is not the TroveManager");
	}
}

contracts/core/StabilityPool.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../dependencies/ZebraOwnable.sol";
import "../dependencies/SystemStart.sol";
import "../dependencies/ZebraMath.sol";
import "../interfaces/IZebraUSD.sol";
import "../interfaces/ICommunityIssuance.sol";
import "../interfaces/IStabilityPool.sol";

/**
    @title Zebra Stability Pool
    @notice Based on Liquity's `StabilityPool`
            https://github.com/liquity/dev/blob/main/packages/contracts/contracts/StabilityPool.sol

            Zebra's implementation is modified to support multiple collaterals. Deposits into
            the stability pool may be used to liquidate any supported collateral type.
 */
contract StabilityPool is IStabilityPool, ZebraOwnable {
	using SafeERC20 for IERC20;

	uint256 public constant DECIMAL_PRECISION = 1e18;
	uint128 public constant SUNSET_DURATION = 180 days;

	IZebraUSD public immutable zebraUSD;
	address public immutable factory;
	address public immutable liquidationManager;
	ICommunityIssuance public immutable communityIssuance;

	mapping(IERC20 => uint256) public indexByCollateral;
	IERC20[] public collateralTokens;

	// Tracker for Debt held in the pool. Changes when users deposit/withdraw, and when Trove debt is offset.
	uint256 internal totalZebraUSDDeposits;
	mapping(address => uint256) public lockTime;
	mapping(address => uint256) public accountDeposits; // depositor address -> initial deposit
	mapping(address => Snapshots) public depositSnapshots; // depositor address -> snapshots struct

	// index values are mapped against the values within `collateralTokens`
	mapping(address => uint256[256]) public depositSums; // depositor address -> sums

	mapping(address => uint256[256]) public collateralGainsByDepositor;

	mapping(address => uint256) private storedPendingReward;

	/*  Product 'P': Running product by which to multiply an initial deposit, in order to find the current compounded deposit,
	 * after a series of liquidations have occurred, each of which cancel some debt with the deposit.
	 *
	 * During its lifetime, a deposit's value evolves from d_t to d_t * P / P_t , where P_t
	 * is the snapshot of P taken at the instant the deposit was made. 18-digit decimal.
	 */
	uint256 public P = DECIMAL_PRECISION;

	uint256 public constant SCALE_FACTOR = 1e9;

	// Each time the scale of P shifts by SCALE_FACTOR, the scale is incremented by 1
	uint128 public currentScale;

	// With each offset that fully empties the Pool, the epoch is incremented by 1
	uint128 public currentEpoch;

	/* collateral Gain sum 'S': During its lifetime, each deposit d_t earns a collateral gain of ( d_t * [S - S_t] )/P_t, where S_t
	 * is the depositor's snapshot of S taken at the time t when the deposit was made.
	 *
	 * The 'S' sums are stored in a nested mapping (epoch => scale => sum):
	 *
	 * - The inner mapping records the sum S at different scales
	 * - The outer mapping records the (scale => sum) mappings, for different epochs.
	 */

	// index values are mapped against the values within `collateralTokens`
	mapping(uint128 => mapping(uint128 => uint256[256])) public epochToScaleToSums;

	/*
	 * Similarly, the sum 'G' is used to calculate esZebra gains. During it's lifetime, each deposit d_t earns a esZebra gain of
	 *  ( d_t * [G - G_t] )/P_t, where G_t is the depositor's snapshot of G taken at time t when  the deposit was made.
	 *
	 *  esZebra reward events occur are triggered by depositor operations (new deposit, topup, withdrawal), and liquidations.
	 *  In each case, the esZebra reward is issued (i.e. G is updated), before other state changes are made.
	 */
	mapping(uint128 => mapping(uint128 => uint256)) public epochToScaleToG;

	// Error tracker for the error correction in the esZebra issuance calculation
	uint256 public lastEsZebraError;
	// Error trackers for the error correction in the offset calculation
	uint256[256] public lastCollateralError_Offset;
	uint256 public lastDebtLossError_Offset;

	mapping(uint16 => SunsetIndex) public _sunsetIndexes;

	Queue public queue;

	struct Snapshots {
		uint256 P;
		uint256 G;
		uint128 scale;
		uint128 epoch;
	}

	struct SunsetIndex {
		uint128 idx;
		uint128 expiry;
	}
	struct Queue {
		uint16 firstSunsetIndexKey;
		uint16 nextSunsetIndexKey;
	}

	constructor(IZebraCore _zebraCore, IZebraUSD _zebraUSDAddress, address _factory, address _liquidationManager, address _communityIssuance) ZebraOwnable(_zebraCore) {
		zebraUSD = _zebraUSDAddress;
		factory = _factory;
		liquidationManager = _liquidationManager;
		communityIssuance = ICommunityIssuance(_communityIssuance);
	}

	function enableCollateral(IERC20 _collateral) external {
		require(msg.sender == factory, "Not factory");
		uint256 length = collateralTokens.length;
		bool collateralEnabled;
		for (uint256 i = 0; i < length; i++) {
			if (collateralTokens[i] == IERC20(_collateral)) {
				collateralEnabled = true;
				break;
			}
		}
		if (!collateralEnabled) {
			Queue memory queueCached = queue;
			if (queueCached.nextSunsetIndexKey > queueCached.firstSunsetIndexKey) {
				SunsetIndex memory sIdx = _sunsetIndexes[queueCached.firstSunsetIndexKey];
				if (sIdx.expiry < block.timestamp) {
					delete _sunsetIndexes[queue.firstSunsetIndexKey++];
					_overwriteCollateral(_collateral, sIdx.idx);
					return;
				}
			}
			collateralTokens.push(_collateral);
			indexByCollateral[_collateral] = collateralTokens.length;
		} else {
			// revert if the factory is trying to deploy a new TM with a sunset collateral
			require(indexByCollateral[_collateral] > 0, "Collateral is sunsetting");
		}
	}

	function _overwriteCollateral(IERC20 _newCollateral, uint256 idx) internal {
		require(indexByCollateral[_newCollateral] == 0, "Collateral must be sunset");
		uint256 length = collateralTokens.length;
		require(idx < length, "Index too large");
		uint256 externalLoopEnd = currentEpoch;
		uint256 internalLoopEnd = currentScale;
		for (uint128 i; i <= externalLoopEnd; ) {
			for (uint128 j; j <= internalLoopEnd; ) {
				epochToScaleToSums[i][j][idx] = 0;
				unchecked {
					++j;
				}
			}
			unchecked {
				++i;
			}
		}
		indexByCollateral[_newCollateral] = idx + 1;
		emit CollateralOverwritten(address(collateralTokens[idx]), address(_newCollateral));
		collateralTokens[idx] = _newCollateral;
	}

	/**
     * @notice Starts sunsetting a collateral
     *         During sunsetting liquidated collateral handoff to the SP will revert
        @dev IMPORTANT: When sunsetting a collateral, `TroveManager.startSunset`
                        should be called on all TM linked to that collateral
        @param collateral Collateral to sunset

     */
	function startCollateralSunset(IERC20 collateral) external onlyOwner {
		require(indexByCollateral[collateral] > 0, "Collateral already sunsetting");
		_sunsetIndexes[queue.nextSunsetIndexKey++] = SunsetIndex(uint128(indexByCollateral[collateral] - 1), uint128(block.timestamp + SUNSET_DURATION));
		delete indexByCollateral[collateral]; //This will prevent calls to the SP in case of liquidations
	}

	function getTotalZebraUSDDeposits() external view returns (uint256) {
		return totalZebraUSDDeposits;
	}

	// --- External Depositor Functions ---

	/*  provideToSP():
	 *
	 * - Triggers a esZebra issuance, based on time passed since the last issuance. The esZebra issuance is shared between *all* depositors and front ends
	 * - Tags the deposit with the provided front end tag param, if it's a new deposit
	 * - Sends depositor's accumulated gains (esZebra, collateral) to depositor
	 * - Sends the tagged front end's accumulated esZebra gains to the tagged front end
	 * - Increases deposit and tagged front end's stake, and takes new snapshots for each.
	 */
	function provideToSP(uint256 _amount) external {
		require(!ZebraCore.paused(), "Deposits are paused");
		require(_amount > 0, "StabilityPool: Amount must be non-zero");
		lockTime[msg.sender] = block.timestamp;
		_triggerRewardIssuance();

		_accrueDepositorCollateralGain(msg.sender);

		uint256 compoundedDeposit = getCompoundedDeposit(msg.sender);

		_accrueRewards(msg.sender);

		zebraUSD.sendToSP(msg.sender, _amount);
		uint256 newTotalZebraUSDDeposits = totalZebraUSDDeposits + _amount;
		totalZebraUSDDeposits = newTotalZebraUSDDeposits;
		emit StabilityPoolZebraUSDBalanceUpdated(newTotalZebraUSDDeposits);

		uint256 newDeposit = compoundedDeposit + _amount;
		accountDeposits[msg.sender] = newDeposit;

		_updateSnapshots(msg.sender, newDeposit);
		emit UserDepositChanged(msg.sender, newDeposit);
	}

	/*  withdrawFromSP():
	 *
	 * - Triggers a esZebra issuance, based on time passed since the last issuance. The esZebra issuance is shared between *all* depositors and front ends
	 * - Removes the deposit's front end tag if it is a full withdrawal
	 * - Sends all depositor's accumulated gains (esZebra, collateral) to depositor
	 * - Sends the tagged front end's accumulated esZebra gains to the tagged front end
	 * - Decreases deposit and tagged front end's stake, and takes new snapshots for each.
	 *
	 * If _amount > userDeposit, the user withdraws all of their compounded deposit.
	 */
	function withdrawFromSP(uint256 _amount) external {
		uint256 initialDeposit = accountDeposits[msg.sender];
		require(initialDeposit > 0, "StabilityPool: User must have a non-zero deposit");
		require(lockTime[msg.sender] != block.timestamp, "StabilityPool: Withdrawal is locked");
		_triggerRewardIssuance();

		_accrueDepositorCollateralGain(msg.sender);

		uint256 compoundedDeposit = getCompoundedDeposit(msg.sender);
		uint256 debtToWithdraw = ZebraMath._min(_amount, compoundedDeposit);

		_accrueRewards(msg.sender);

		if (debtToWithdraw > 0) {
			zebraUSD.returnFromPool(address(this), msg.sender, debtToWithdraw);
			_decreaseDebt(debtToWithdraw);
		}

		// Update deposit
		uint256 newDeposit = compoundedDeposit - debtToWithdraw;
		accountDeposits[msg.sender] = newDeposit;

		_updateSnapshots(msg.sender, newDeposit);
		emit UserDepositChanged(msg.sender, newDeposit);
	}

	function claim(address recipient, uint256[] memory collateralIndexes) external {
		uint256 initialDeposit = accountDeposits[msg.sender];
		require(initialDeposit > 0, "StabilityPool: User must have a non-zero deposit");

		_triggerRewardIssuance();

		_accrueDepositorCollateralGain(msg.sender);

		uint256 compoundedDeposit = getCompoundedDeposit(msg.sender);
		_accrueRewards(msg.sender);

		// Update deposit
		uint256 newDeposit = compoundedDeposit;
		accountDeposits[msg.sender] = newDeposit;

		_updateSnapshots(msg.sender, newDeposit);
		emit UserDepositChanged(msg.sender, newDeposit);
		uint256 amount = _claimReward(msg.sender);
		if (amount > 0) {
			communityIssuance.sendEsZebra(recipient, amount);
		}
		emit RewardClaimed(msg.sender, recipient, amount);
		_claimCollateralGains(recipient, collateralIndexes);
	}

	// --- esZebra issuance functions ---

	function _triggerRewardIssuance() internal {
		_updateG(communityIssuance.issueEsZebra());
	}

	function _updateG(uint256 _esZebraIssuance) internal {
		uint256 totalDebt = totalZebraUSDDeposits; // cached to save an SLOAD
		/*
		 * When total deposits is 0, G is not updated. In this case, the Zebra issued can not be obtained by later
		 * depositors - it is missed out on, and remains in the balanceof the Treasury contract.
		 *
		 */
		if (totalDebt == 0 || _esZebraIssuance == 0) {
			return;
		}

		uint256 esZebraPerUnitStaked = _computeEsZebraPerUnitStaked(_esZebraIssuance, totalDebt);
		uint128 currentEpochCached = currentEpoch;
		uint128 currentScaleCached = currentScale;
		uint256 marginalZebraGain = esZebraPerUnitStaked * P;
		uint256 newG = epochToScaleToG[currentEpochCached][currentScaleCached] + marginalZebraGain;
		epochToScaleToG[currentEpochCached][currentScaleCached] = newG;

		emit G_Updated(newG, currentEpochCached, currentScaleCached);
	}

	function _computeEsZebraPerUnitStaked(uint256 _esZebraIssuance, uint256 _totalZebraUSDDeposits) internal returns (uint256) {
		/*
		 * Calculate the esZebra-per-unit staked.  Division uses a "feedback" error correction, to keep the
		 * cumulative error low in the running total G:
		 *
		 * 1) Form a numerator which compensates for the floor division error that occurred the last time this
		 * function was called.
		 * 2) Calculate "per-unit-staked" ratio.
		 * 3) Multiply the ratio back by its denominator, to reveal the current floor division error.
		 * 4) Store this error for use in the next correction when this function is called.
		 * 5) Note: static analysis tools complain about this "division before multiplication", however, it is intended.
		 */
		uint256 esZebraNumerator = (_esZebraIssuance * DECIMAL_PRECISION) + lastEsZebraError;

		uint256 esZebraPerUnitStaked = esZebraNumerator / _totalZebraUSDDeposits;
		lastEsZebraError = esZebraNumerator - (esZebraPerUnitStaked * _totalZebraUSDDeposits);

		return esZebraPerUnitStaked;
	}

	// --- Liquidation functions ---

	/*
	 * Cancels out the specified debt against the Debt contained in the Stability Pool (as far as possible)
	 */
	function offset(IERC20 collateral, uint256 _debtToOffset, uint256 _collToAdd) external virtual {
		_offset(collateral, _debtToOffset, _collToAdd);
	}

	function _offset(IERC20 collateral, uint256 _debtToOffset, uint256 _collToAdd) internal {
		require(msg.sender == liquidationManager, "StabilityPool: Caller is not Liquidation Manager");
		uint256 idx = indexByCollateral[collateral];
		idx -= 1;

		uint256 totalDebt = totalZebraUSDDeposits; // cached to save an SLOAD
		if (totalDebt == 0 || _debtToOffset == 0) {
			return;
		}

		_triggerRewardIssuance();

		(uint256 collateralGainPerUnitStaked, uint256 debtLossPerUnitStaked) = _computeRewardsPerUnitStaked(_collToAdd, _debtToOffset, totalDebt, idx);

		_updateRewardSumAndProduct(collateralGainPerUnitStaked, debtLossPerUnitStaked, idx); // updates S and P

		// Cancel the liquidated Debt debt with the Debt in the stability pool
		_decreaseDebt(_debtToOffset);
	}

	// --- Offset helper functions ---

	function _computeRewardsPerUnitStaked(uint256 _collToAdd, uint256 _debtToOffset, uint256 _totalZebraUSDDeposits, uint256 idx) internal returns (uint256 collateralGainPerUnitStaked, uint256 debtLossPerUnitStaked) {
		/*
		 * Compute the Debt and collateral rewards. Uses a "feedback" error correction, to keep
		 * the cumulative error in the P and S state variables low:
		 *
		 * 1) Form numerators which compensate for the floor division errors that occurred the last time this
		 * function was called.
		 * 2) Calculate "per-unit-staked" ratios.
		 * 3) Multiply each ratio back by its denominator, to reveal the current floor division error.
		 * 4) Store these errors for use in the next correction when this function is called.
		 * 5) Note: static analysis tools complain about this "division before multiplication", however, it is intended.
		 */
		uint256 collateralNumerator = (_collToAdd * DECIMAL_PRECISION) + lastCollateralError_Offset[idx];

		if (_debtToOffset == _totalZebraUSDDeposits) {
			debtLossPerUnitStaked = DECIMAL_PRECISION; // When the Pool depletes to 0, so does each deposit
			lastDebtLossError_Offset = 0;
		} else {
			uint256 debtLossNumerator = (_debtToOffset * DECIMAL_PRECISION) - lastDebtLossError_Offset;
			/*
			 * Add 1 to make error in quotient positive. We want "slightly too much" Debt loss,
			 * which ensures the error in any given CompoundedDeposit favors the Stability Pool.
			 */
			debtLossPerUnitStaked = (debtLossNumerator / _totalZebraUSDDeposits) + 1;
			lastDebtLossError_Offset = (debtLossPerUnitStaked * _totalZebraUSDDeposits) - debtLossNumerator;
		}

		collateralGainPerUnitStaked = collateralNumerator / _totalZebraUSDDeposits;
		lastCollateralError_Offset[idx] = collateralNumerator - (collateralGainPerUnitStaked * _totalZebraUSDDeposits);

		return (collateralGainPerUnitStaked, debtLossPerUnitStaked);
	}

	// Update the Stability Pool reward sum S and product P
	function _updateRewardSumAndProduct(uint256 _collateralGainPerUnitStaked, uint256 _debtLossPerUnitStaked, uint256 idx) internal {
		uint256 currentP = P;
		uint256 newP;

		/*
		 * The newProductFactor is the factor by which to change all deposits, due to the depletion of Stability Pool Debt in the liquidation.
		 * We make the product factor 0 if there was a pool-emptying. Otherwise, it is (1 - DebtLossPerUnitStaked)
		 */
		uint256 newProductFactor = uint256(DECIMAL_PRECISION) - _debtLossPerUnitStaked;

		uint128 currentScaleCached = currentScale;
		uint128 currentEpochCached = currentEpoch;
		uint256 currentS = epochToScaleToSums[currentEpochCached][currentScaleCached][idx];

		/*
		 * Calculate the new S first, before we update P.
		 * The collateral gain for any given depositor from a liquidation depends on the value of their deposit
		 * (and the value of totalDeposits) prior to the Stability being depleted by the debt in the liquidation.
		 *
		 * Since S corresponds to collateral gain, and P to deposit loss, we update S first.
		 */
		uint256 marginalCollateralGain = _collateralGainPerUnitStaked * currentP;
		uint256 newS = currentS + marginalCollateralGain;
		epochToScaleToSums[currentEpochCached][currentScaleCached][idx] = newS;
		emit S_Updated(idx, newS, currentEpochCached, currentScaleCached);

		// If the Stability Pool was emptied, increment the epoch, and reset the scale and product P
		if (newProductFactor == 0) {
			currentEpoch = currentEpochCached + 1;
			emit EpochUpdated(currentEpoch);
			currentScale = 0;
			emit ScaleUpdated(currentScale);
			newP = DECIMAL_PRECISION;

			// If multiplying P by a non-zero product factor would reduce P below the scale boundary, increment the scale
		} else if ((currentP * newProductFactor) / DECIMAL_PRECISION < SCALE_FACTOR) {
			newP = (currentP * newProductFactor * SCALE_FACTOR) / DECIMAL_PRECISION;
			currentScale = currentScaleCached + 1;
			emit ScaleUpdated(currentScale);
		} else {
			newP = (currentP * newProductFactor) / DECIMAL_PRECISION;
		}

		require(newP > 0, "NewP");
		P = newP;
		emit P_Updated(newP);
	}

	function _decreaseDebt(uint256 _amount) internal {
		uint256 newTotalZebraUSDDeposits = totalZebraUSDDeposits - _amount;
		totalZebraUSDDeposits = newTotalZebraUSDDeposits;
		emit StabilityPoolZebraUSDBalanceUpdated(newTotalZebraUSDDeposits);
	}

	// --- Reward calculator functions for depositor and front end ---

	/* Calculates the collateral gain earned by the deposit since its last snapshots were taken.
	 * Given by the formula:  E = d0 * (S - S(0))/P(0)
	 * where S(0) and P(0) are the depositor's snapshots of the sum S and product P, respectively.
	 * d0 is the last recorded deposit value.
	 */
	function getDepositorCollateralGain(address _depositor) external view returns (uint256[] memory collateralGains) {
		collateralGains = new uint256[](collateralTokens.length);

		uint256 P_Snapshot = depositSnapshots[_depositor].P;
		if (P_Snapshot == 0) return collateralGains;
		uint256[256] memory depositorGains = collateralGainsByDepositor[_depositor];
		uint256 initialDeposit = accountDeposits[_depositor];
		uint128 epochSnapshot = depositSnapshots[_depositor].epoch;
		uint128 scaleSnapshot = depositSnapshots[_depositor].scale;
		uint256[256] memory sums = epochToScaleToSums[epochSnapshot][scaleSnapshot];
		uint256[256] memory nextSums = epochToScaleToSums[epochSnapshot][scaleSnapshot + 1];
		uint256[256] memory depSums = depositSums[_depositor];

		for (uint256 i = 0; i < collateralGains.length; i++) {
			collateralGains[i] = depositorGains[i];
			if (sums[i] == 0) continue; // Collateral was overwritten or not gains
			uint256 firstPortion = sums[i] - depSums[i];
			uint256 secondPortion = nextSums[i] / SCALE_FACTOR;
			collateralGains[i] += (initialDeposit * (firstPortion + secondPortion)) / P_Snapshot / DECIMAL_PRECISION;
		}
		return collateralGains;
	}

	function _accrueDepositorCollateralGain(address _depositor) private returns (bool hasGains) {
		uint256[256] storage depositorGains = collateralGainsByDepositor[_depositor];
		uint256 collaterals = collateralTokens.length;
		uint256 initialDeposit = accountDeposits[_depositor];
		hasGains = false;
		if (initialDeposit == 0) {
			return hasGains;
		}

		uint128 epochSnapshot = depositSnapshots[_depositor].epoch;
		uint128 scaleSnapshot = depositSnapshots[_depositor].scale;
		uint256 P_Snapshot = depositSnapshots[_depositor].P;

		uint256[256] storage sums = epochToScaleToSums[epochSnapshot][scaleSnapshot];
		uint256[256] storage nextSums = epochToScaleToSums[epochSnapshot][scaleSnapshot + 1];
		uint256[256] storage depSums = depositSums[_depositor];

		for (uint256 i = 0; i < collaterals; i++) {
			if (sums[i] == 0) continue; // Collateral was overwritten or not gains
			hasGains = true;
			uint256 firstPortion = sums[i] - depSums[i];
			uint256 secondPortion = nextSums[i] / SCALE_FACTOR;
			depositorGains[i] += (initialDeposit * (firstPortion + secondPortion)) / P_Snapshot / DECIMAL_PRECISION;
		}
		return (hasGains);
	}

	/*
	 * Calculate the esZebra gain earned by a deposit since its last snapshots were taken.
	 * Given by the formula:  esZebra = d0 * (G - G(0))/P(0)
	 * where G(0) and P(0) are the depositor's snapshots of the sum G and product P, respectively.
	 * d0 is the last recorded deposit value.
	 */
	function claimableReward(address _depositor) external view returns (uint256) {
		uint256 totalDebt = totalZebraUSDDeposits;
		uint256 initialDeposit = accountDeposits[_depositor];

		if (totalDebt == 0 || initialDeposit == 0) {
			return 0;
		}
		uint256 esZebraNumerator = (communityIssuance.pendingReward() * DECIMAL_PRECISION) + lastEsZebraError;
		uint256 esZebraPerUnitStaked = esZebraNumerator / totalDebt;
		uint256 marginalEsZebraGain = esZebraPerUnitStaked * P;

		Snapshots memory snapshots = depositSnapshots[_depositor];
		uint128 epochSnapshot = snapshots.epoch;
		uint128 scaleSnapshot = snapshots.scale;
		uint256 firstPortion;
		uint256 secondPortion;
		if (scaleSnapshot == currentScale) {
			firstPortion = epochToScaleToG[epochSnapshot][scaleSnapshot] - snapshots.G + marginalEsZebraGain;
			secondPortion = epochToScaleToG[epochSnapshot][scaleSnapshot + 1] / SCALE_FACTOR;
		} else {
			firstPortion = epochToScaleToG[epochSnapshot][scaleSnapshot] - snapshots.G;
			secondPortion = (epochToScaleToG[epochSnapshot][scaleSnapshot + 1] + marginalEsZebraGain) / SCALE_FACTOR;
		}

		return (initialDeposit * (firstPortion + secondPortion)) / snapshots.P / DECIMAL_PRECISION;
	}

	function _claimableReward(address _depositor) private view returns (uint256) {
		uint256 initialDeposit = accountDeposits[_depositor];
		if (initialDeposit == 0) {
			return 0;
		}

		Snapshots memory snapshots = depositSnapshots[_depositor];

		return _getEsZebraGainFromSnapshots(initialDeposit, snapshots);
	}

	function _getEsZebraGainFromSnapshots(uint256 initialStake, Snapshots memory snapshots) internal view returns (uint256) {
		/*
		 * Grab the sum 'G' from the epoch at which the stake was made. The esZebra gain may span up to one scale change.
		 * If it does, the second portion of the esZebra gain is scaled by 1e9.
		 * If the gain spans no scale change, the second portion will be 0.
		 */
		uint128 epochSnapshot = snapshots.epoch;
		uint128 scaleSnapshot = snapshots.scale;
		uint256 G_Snapshot = snapshots.G;
		uint256 P_Snapshot = snapshots.P;

		uint256 firstPortion = epochToScaleToG[epochSnapshot][scaleSnapshot] - G_Snapshot;
		uint256 secondPortion = epochToScaleToG[epochSnapshot][scaleSnapshot + 1] / SCALE_FACTOR;

		uint256 esZebraGain = (initialStake * (firstPortion + secondPortion)) / P_Snapshot / DECIMAL_PRECISION;

		return esZebraGain;
	}

	// --- Compounded deposit and compounded front end stake ---

	/*
	 * Return the user's compounded deposit. Given by the formula:  d = d0 * P/P(0)
	 * where P(0) is the depositor's snapshot of the product P, taken when they last updated their deposit.
	 */
	function getCompoundedDeposit(address _depositor) public view returns (uint256) {
		uint256 initialDeposit = accountDeposits[_depositor];
		if (initialDeposit == 0) {
			return 0;
		}

		Snapshots memory snapshots = depositSnapshots[_depositor];

		uint256 compoundedDeposit = _getCompoundedStakeFromSnapshots(initialDeposit, snapshots);
		return compoundedDeposit;
	}

	// Internal function, used to calculcate compounded deposits and compounded front end stakes.
	function _getCompoundedStakeFromSnapshots(uint256 initialStake, Snapshots memory snapshots) internal view returns (uint256) {
		uint256 snapshot_P = snapshots.P;
		uint128 scaleSnapshot = snapshots.scale;
		uint128 epochSnapshot = snapshots.epoch;

		// If stake was made before a pool-emptying event, then it has been fully cancelled with debt -- so, return 0
		if (epochSnapshot < currentEpoch) {
			return 0;
		}

		uint256 compoundedStake;
		uint128 scaleDiff = currentScale - scaleSnapshot;

		/* Compute the compounded stake. If a scale change in P was made during the stake's lifetime,
		 * account for it. If more than one scale change was made, then the stake has decreased by a factor of
		 * at least 1e-9 -- so return 0.
		 */
		if (scaleDiff == 0) {
			compoundedStake = (initialStake * P) / snapshot_P;
		} else if (scaleDiff == 1) {
			compoundedStake = (initialStake * P) / snapshot_P / SCALE_FACTOR;
		} else {
			// if scaleDiff >= 2
			compoundedStake = 0;
		}

		/*
		 * If compounded deposit is less than a billionth of the initial deposit, return 0.
		 *
		 * NOTE: originally, this line was in place to stop rounding errors making the deposit too large. However, the error
		 * corrections should ensure the error in P "favors the Pool", i.e. any given compounded deposit should slightly less
		 * than it's theoretical value.
		 *
		 * Thus it's unclear whether this line is still really needed.
		 */
		if (compoundedStake < initialStake / 1e9) {
			return 0;
		}

		return compoundedStake;
	}

	function _claimCollateralGains(address recipient, uint256[] memory collateralIndexes) internal {
		uint256 loopEnd = collateralIndexes.length;
		uint256[] memory collateralGains = new uint256[](collateralTokens.length);

		uint256[256] storage depositorGains = collateralGainsByDepositor[msg.sender];
		for (uint256 i; i < loopEnd; ) {
			uint256 collateralIndex = collateralIndexes[i];
			uint256 gains = depositorGains[collateralIndex];
			if (gains > 0) {
				collateralGains[collateralIndex] = gains;
				depositorGains[collateralIndex] = 0;
				collateralTokens[collateralIndex].safeTransfer(recipient, gains);
			}
			unchecked {
				++i;
			}
		}
		emit CollateralGainWithdrawn(msg.sender, collateralGains);
	}

	// --- Stability Pool Deposit Functionality ---

	function _updateSnapshots(address _depositor, uint256 _newValue) internal {
		uint256 length;
		if (_newValue == 0) {
			delete depositSnapshots[_depositor];

			length = collateralTokens.length;
			for (uint256 i = 0; i < length; i++) {
				depositSums[_depositor][i] = 0;
			}
			emit DepositSnapshotUpdated(_depositor, 0, 0);
			return;
		}
		uint128 currentScaleCached = currentScale;
		uint128 currentEpochCached = currentEpoch;
		uint256 currentP = P;

		// Get S and G for the current epoch and current scale
		uint256[256] storage currentS = epochToScaleToSums[currentEpochCached][currentScaleCached];
		uint256 currentG = epochToScaleToG[currentEpochCached][currentScaleCached];

		// Record new snapshots of the latest running product P, sum S, and sum G, for the depositor
		depositSnapshots[_depositor].P = currentP;
		depositSnapshots[_depositor].G = currentG;
		depositSnapshots[_depositor].scale = currentScaleCached;
		depositSnapshots[_depositor].epoch = currentEpochCached;

		length = collateralTokens.length;
		for (uint256 i = 0; i < length; i++) {
			depositSums[_depositor][i] = currentS[i];
		}

		emit DepositSnapshotUpdated(_depositor, currentP, currentG);
	}

	//This assumes the snapshot gets updated in the caller
	function _accrueRewards(address _depositor) internal {
		uint256 amount = _claimableReward(_depositor);
		storedPendingReward[_depositor] = storedPendingReward[_depositor] + amount;
	}

	function _claimReward(address account) internal returns (uint256 amount) {
		uint256 initialDeposit = accountDeposits[account];

		if (initialDeposit > 0) {
			_triggerRewardIssuance();
			bool hasGains = _accrueDepositorCollateralGain(account);

			uint256 compoundedDeposit = getCompoundedDeposit(account);
			uint256 debtLoss = initialDeposit - compoundedDeposit;

			amount = _claimableReward(account);
			// we update only if the snapshot has changed
			if (debtLoss > 0 || hasGains || amount > 0) {
				// Update deposit
				uint256 newDeposit = compoundedDeposit;
				accountDeposits[account] = newDeposit;
				_updateSnapshots(account, newDeposit);
			}
		}
		uint256 pending = storedPendingReward[account];
		if (pending > 0) {
			amount += pending;
			storedPendingReward[account] = 0;
		}
	}
}

contracts/core/TroveManager.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "../interfaces/IBorrowerOperations.sol";
import "../interfaces/IZebraUSD.sol";
import "../interfaces/ITroveManager.sol";
import "../dependencies/ZebraBase.sol";
import "../dependencies/ZebraOwnable.sol";
import "../dependencies/SystemStart.sol";
import "../dependencies/ZebraMath.sol";
import "./InterestDebtPool.sol";

/**
    @title Zebra Trove Manager
    @notice Based on Liquity's `TroveManager`
            https://github.com/liquity/dev/blob/main/packages/contracts/contracts/TroveManager.sol

            Zebra's implementation is modified so that multiple `TroveManager` and `SortedTroves`
            contracts are deployed in tandem, with each pair managing troves of a single collateral
            type.

            Functionality related to liquidations has been moved to `LiquidationManager`. This was
            necessary to avoid the restriction on deployed bytecode size.
 */
contract TroveManager is ITroveManager, InterestDebtPool, ZebraBase, ZebraOwnable, SystemStart {
	using SafeERC20 for IERC20;

	// --- Connected contract declarations ---
	address public immutable borrowerOperationsAddress;
	address public immutable liquidationManager;
	address public immutable gasPoolAddress;

	IPriceFeed public override priceFeed;
	IERC20 public collateralToken;

	// A doubly linked list of Troves, sorted by their collateral ratios
	ISortedTroves public override sortedTroves;

	// Minimum collateral ratio for individual troves
	uint256 public MCR;

	uint256 internal constant SECONDS_IN_ONE_MINUTE = 60;

	// During bootsrap period redemptions are not allowed
	uint256 public constant BOOTSTRAP_PERIOD = 14 days;
	uint32 public constant SUNSETTING_INTEREST_RATE = 5e5; //50%

	/*
	 * BETA: 18 digit decimal. Parameter by which to divide the redeemed fraction, in order to calc the new base rate from a redemption.
	 * Corresponds to (1 / ALPHA) in the white paper.
	 */
	uint256 internal constant BETA = 2;

	// commented values are Liquity's fixed settings for each parameter
	uint256 public minuteDecayFactor; // 999037758833783000  (half-life of 12 hours)
	uint256 public redemptionFeeFloor; // DECIMAL_PRECISION / 1000 * 5  (0.5%)
	uint256 public maxRedemptionFee; // DECIMAL_PRECISION  (100%)
	uint256 public borrowingFeeFloor; // DECIMAL_PRECISION / 1000 * 5  (0.5%)
	uint256 public maxBorrowingFee; // DECIMAL_PRECISION / 100 * 5  (5%)
	uint256 public maxSystemDebt;

	uint256 public systemDeploymentTime;
	bool public paused;
	bool public sunsetting;

	uint256 public baseRate;

	// The timestamp of the latest fee operation (redemption or new debt issuance)
	uint256 public lastFeeOperationTime;

	uint256 public totalStakes;

	// Snapshot of the value of totalStakes, taken immediately after the latest liquidation
	uint256 public totalStakesSnapshot;

	// Snapshot of the total collateral taken immediately after the latest liquidation.
	uint256 public totalCollateralSnapshot;

	/*
	 * L_collateral and L_debt track the sums of accumulated liquidation rewards per unit staked. During its lifetime, each stake earns:
	 *
	 * An collateral gain of ( stake * [L_collateral - L_collateral(0)] )
	 * A debt increase  of ( stake * [L_debt - L_debt(0)] )
	 *
	 * Where L_collateral(0) and L_debt(0) are snapshots of L_collateral and L_debt for the active Trove taken at the instant the stake was made
	 */
	uint256 public L_collateral;
	uint256 public L_debt;

	// Error trackers for the trove redistribution calculation
	uint256 public lastCollateralError_Redistribution;
	uint256 public lastDebtError_Redistribution;

	uint256 internal totalActiveCollateral;
	uint256 internal totalActiveDebt;
	uint256 public interestPayable;

	uint256 public defaultedCollateral;
	uint256 public defaultedDebt;

	uint256 public rewardIntegral;
	uint128 public rewardRate;
	uint32 public periodFinish;

	mapping(address => Trove) internal Troves;

	mapping(address => RewardSnapshot) internal rewardSnapshots;

	mapping(address => uint256) public surplusBalances;

	// Map addresses with active troves to their RewardSnapshot

	// Array of all active trove addresses - used to to compute an approximate hint off-chain, for the sorted list insertion
	address[] public TroveOwners;

	modifier whenNotPaused() {
		require(!paused, "Collateral Paused");
		_;
	}

	constructor(
		IZebraCore _zebraCore,
		address _gasPoolAddress,
		address _zebraUSDAddress,
		address _borrowerOperationsAddress,
		address _liquidationManager,
		uint256 _gasCompensation
	) InterestDebtPool(_zebraUSDAddress) ZebraOwnable(_zebraCore) ZebraBase(_gasCompensation) SystemStart(_zebraCore) {
		gasPoolAddress = _gasPoolAddress;
		borrowerOperationsAddress = _borrowerOperationsAddress;
		liquidationManager = _liquidationManager;
	}

	function setAddresses(address _priceFeedAddress, address _sortedTrovesAddress, IERC20 _collateralToken) external override {
		assert(address(sortedTroves) == address(0));
		priceFeed = IPriceFeed(_priceFeedAddress);
		sortedTroves = ISortedTroves(_sortedTrovesAddress);
		collateralToken = IERC20(_collateralToken);
		sunsetting = false;

		systemDeploymentTime = block.timestamp;
	}

	function feeReceiver() public view override returns (address) {
		return ZebraCore.feeReceiver();
	}

	function startSunset() external onlyOwner {
		_distributeInterestDebt();
		sunsetting = true;
		interestRate = SUNSETTING_INTEREST_RATE;
		redemptionFeeFloor = 0;
		maxSystemDebt = 0;
	}

	/**
	 * @notice Sets the pause state for this trove manager
	 *         Pausing is used to mitigate risks in exceptional circumstances
	 *         Functionalities affected by pausing are:
	 *         - New borrowing is not possible
	 *         - New collateral deposits are not possible
	 * @param _paused If true the protocol is paused
	 */
	function setPaused(bool _paused) external {
		require((_paused && msg.sender == guardian()) || msg.sender == owner(), "Unauthorized");
		paused = _paused;
	}

	/**
	 * @notice Sets a custom price feed for this trove manager
	 * @param _priceFeedAddress Price feed address
	 */
	function setPriceFeed(address _priceFeedAddress) external onlyOwner {
		priceFeed = IPriceFeed(_priceFeedAddress);
	}

	function setInterestRate(uint32 _interestRate) external onlyOwner {
		assert(_interestRate <= MAXFP / 10);
		_distributeInterestDebt();
		interestRate = _interestRate;
	}

	function setMCR(uint256 _MCR) external onlyOwner {
		require(_MCR <= CCR && _MCR >= 1100000000000000000, "MCR cannot be > CCR or < 110%");
		MCR = _MCR;
	}

	function setMaxSystemDebt(uint256 _maxSystemDebt) external onlyOwner {
		require(_maxSystemDebt > getEntireSystemDebt(), "new max system debt must be greater than current system debt");
		maxSystemDebt = _maxSystemDebt;
	}

	/*
        _minuteDecayFactor is calculated as

            10**18 * (1/2)**(1/n)

        where n = the half-life in minutes
     */
	function setParameters(uint256 _minuteDecayFactor, uint256 _redemptionFeeFloor, uint256 _maxRedemptionFee, uint256 _borrowingFeeFloor, uint256 _maxBorrowingFee, uint256 _maxSystemDebt, uint256 _MCR, uint32 _interestRate) public {
		require(!sunsetting, "Cannot change after sunset");
		require(_MCR <= CCR && _MCR >= 1100000000000000000, "MCR cannot be > CCR or < 110%");
		if (minuteDecayFactor != 0) {
			require(msg.sender == owner(), "Only owner");
		}
		assert(
			_minuteDecayFactor >= 977159968434245000 && // half-life of 30 minutes
				_minuteDecayFactor <= 999931237762985000 // half-life of 1 week
		);
		assert(_redemptionFeeFloor <= _maxRedemptionFee && _maxRedemptionFee <= DECIMAL_PRECISION);
		assert(_borrowingFeeFloor <= _maxBorrowingFee && _maxBorrowingFee <= DECIMAL_PRECISION);
		assert(_interestRate <= MAXFP / 10);
		_distributeInterestDebt();
		_decayBaseRate();

		minuteDecayFactor = _minuteDecayFactor;
		redemptionFeeFloor = _redemptionFeeFloor;
		maxRedemptionFee = _maxRedemptionFee;
		borrowingFeeFloor = _borrowingFeeFloor;
		maxBorrowingFee = _maxBorrowingFee;
		maxSystemDebt = _maxSystemDebt;

		MCR = _MCR;
		interestRate = _interestRate;
	}

	function distributeInterestDebt() public returns (uint256) {
		return _distributeInterestDebt();
	}

	// --- Getters ---

	function fetchPrice() public returns (uint256) {
		IPriceFeed _priceFeed = priceFeed;
		return _priceFeed.fetchPrice(address(collateralToken));
	}

	function getTrove(address _borrower) external view override returns (Trove memory) {
		return Troves[_borrower];
	}

	function getRewardSnapshots(address _borrower) external view override returns (RewardSnapshot memory) {
		return rewardSnapshots[_borrower];
	}

	function getTroveOwnersCount() external view returns (uint256) {
		return TroveOwners.length;
	}

	function getTroveFromTroveOwnersArray(uint256 _index) external view returns (address) {
		return TroveOwners[_index];
	}

	function getTroveStatus(address _borrower) external view returns (uint256) {
		return uint256(Troves[_borrower].status);
	}

	function getTroveStake(address _borrower) external view returns (uint256) {
		return Troves[_borrower].stake;
	}

	/**
        @notice Get the current total collateral and debt amounts for a trove
        @dev Also includes pending rewards from redistribution
     */
	function getTroveCollAndDebt(address _borrower) public view returns (uint256 coll, uint256 debt) {
		(debt, coll, , ) = getEntireDebtAndColl(_borrower);
		return (coll, debt);
	}

	/**
        @notice Get the total and pending collateral and debt amounts for a trove
        @dev Used by the liquidation manager
     */
	function getEntireDebtAndColl(address _borrower) public view returns (uint256 debt, uint256 coll, uint256 pendingDebtReward, uint256 pendingCollateralReward) {
		Trove storage t = Troves[_borrower];
		debt = t.debt;
		coll = t.coll;
		(pendingCollateralReward, pendingDebtReward) = getPendingCollAndDebtRewards(_borrower);
		debt = debt + pendingDebtReward;
		coll = coll + pendingCollateralReward;
	}

	function getEntireSystemColl() public view returns (uint256) {
		return totalActiveCollateral + defaultedCollateral;
	}

	function getEntireSystemDebt() public view override(ITroveManager, InterestDebtPool) returns (uint256) {
		return totalActiveDebt + defaultedDebt;
	}

	function getGlobalSystemDebt() public view returns (uint256) {
		return totalActiveDebt + defaultedDebt + outstandingInterestDebt + getPendingSystemInterestDebt();
	}

	function getEntireSystemBalances() external returns (uint256, uint256, uint256) {
		return (getEntireSystemColl(), getGlobalSystemDebt(), fetchPrice());
	}

	// --- Helper functions ---

	// Return the nominal collateral ratio (ICR) of a given Trove, without the price. Takes a trove's pending coll and debt rewards from redistributions into account.
	function getNominalICR(address _borrower) public view returns (uint256) {
		(uint256 currentCollateral, uint256 currentDebt) = getTroveCollAndDebt(_borrower);

		uint256 NICR = ZebraMath._computeNominalCR(currentCollateral, currentDebt);
		return NICR;
	}

	function getRedemptionICR(address _borrower, uint256 _price) public view returns (uint256) {
		(uint256 currentCollateral, uint256 currentDebt) = getTroveCollAndDebt(_borrower);
		uint256 ICR = ZebraMath._computeCR(currentCollateral, currentDebt, _price);
		return ICR;
	}

	// Return the current collateral ratio (ICR) of a given Trove. Takes a trove's pending coll and debt rewards from redistributions into account.
	function getCurrentICR(address _borrower, uint256 _price) public view returns (uint256) {
		(uint256 currentCollateral, uint256 currentDebt) = getTroveCollAndDebt(_borrower);
		uint256 ICR = ZebraMath._computeCR(currentCollateral, currentDebt + getTroveInterest(_borrower, currentDebt), _price);
		return ICR;
	}

	function getTroveInterest(address _borrower, uint256 debt) public view returns (uint256) {
		return (debt * (getPendingInterestDebt() - rewardSnapshots[_borrower].interest)) / DECIMAL_PRECISION;
	}

	function getTotalActiveCollateral() public view returns (uint256) {
		return totalActiveCollateral;
	}

	function getTotalActiveDebt() public view returns (uint256) {
		return totalActiveDebt;
	}

	// Get the borrower's pending accumulated collateral and debt rewards, earned by their stake
	function getPendingCollAndDebtRewards(address _borrower) public view returns (uint256, uint256) {
		RewardSnapshot memory snapshot = rewardSnapshots[_borrower];

		uint256 coll = L_collateral - snapshot.collateral;
		uint256 debt = L_debt - snapshot.debt;

		if (coll + debt == 0 || Troves[_borrower].status != Status.active) return (0, 0);

		uint256 stake = Troves[_borrower].stake;
		return ((stake * coll) / DECIMAL_PRECISION, (stake * debt) / DECIMAL_PRECISION);
	}

	function hasPendingRewards(address _borrower) public view returns (bool) {
		/*
		 * A Trove has pending rewards if its snapshot is less than the current rewards per-unit-staked sum:
		 * this indicates that rewards have occured since the snapshot was made, and the user therefore has
		 * pending rewards
		 */
		if (Troves[_borrower].status != Status.active) {
			return false;
		}

		return (rewardSnapshots[_borrower].collateral < L_collateral);
	}

	// --- Redemption fee functions ---

	/*
	 * This function has two impacts on the baseRate state variable:
	 * 1) decays the baseRate based on time passed since last redemption or debt borrowing operation.
	 * then,
	 * 2) increases the baseRate based on the amount redeemed, as a proportion of total supply
	 */
	function _updateBaseRateFromRedemption(uint256 _collateralDrawn, uint256 _price, uint256 _totalDebtSupply) internal returns (uint256) {
		uint256 decayedBaseRate = _calcDecayedBaseRate();

		/* Convert the drawn collateral back to debt at face value rate (1 debt:1 USD), in order to get
		 * the fraction of total supply that was redeemed at face value. */
		uint256 redeemedDebtFraction = (_collateralDrawn * _price) / _totalDebtSupply;
		uint256 newBaseRate = decayedBaseRate + (redeemedDebtFraction / BETA);
		newBaseRate = ZebraMath._min(newBaseRate, DECIMAL_PRECISION); // cap baseRate at a maximum of 100%

		// Update the baseRate state variable
		baseRate = newBaseRate;
		emit BaseRateUpdated(newBaseRate);

		_updateLastFeeOpTime();

		return newBaseRate;
	}

	function getRedemptionRate() public view returns (uint256) {
		return _calcRedemptionRate(baseRate);
	}

	function getRedemptionRateWithDecay() public view returns (uint256) {
		return _calcRedemptionRate(_calcDecayedBaseRate());
	}

	function _calcRedemptionRate(uint256 _baseRate) internal view returns (uint256) {
		return
			ZebraMath._min(
				redemptionFeeFloor + _baseRate,
				maxRedemptionFee // cap at a maximum of 100%
			);
	}

	function getRedemptionFeeWithDecay(uint256 _collateralDrawn) external view returns (uint256) {
		return _calcRedemptionFee(getRedemptionRateWithDecay(), _collateralDrawn);
	}

	function _calcRedemptionFee(uint256 _redemptionRate, uint256 _collateralDrawn) internal pure returns (uint256) {
		uint256 redemptionFee = (_redemptionRate * _collateralDrawn) / DECIMAL_PRECISION;
		require(redemptionFee < _collateralDrawn, "Fee exceeds returned collateral");
		return redemptionFee;
	}

	// --- Borrowing fee functions ---

	function getBorrowingRate() public view returns (uint256) {
		return _calcBorrowingRate(baseRate);
	}

	function getBorrowingRateWithDecay() public view returns (uint256) {
		return _calcBorrowingRate(_calcDecayedBaseRate());
	}

	function _calcBorrowingRate(uint256 _baseRate) internal view returns (uint256) {
		return ZebraMath._min(borrowingFeeFloor + _baseRate, maxBorrowingFee);
	}

	function getBorrowingFee(uint256 _debt) external view returns (uint256) {
		return _calcBorrowingFee(getBorrowingRate(), _debt);
	}

	function getBorrowingFeeWithDecay(uint256 _debt) external view returns (uint256) {
		return _calcBorrowingFee(getBorrowingRateWithDecay(), _debt);
	}

	function _calcBorrowingFee(uint256 _borrowingRate, uint256 _debt) internal pure returns (uint256) {
		return (_borrowingRate * _debt) / DECIMAL_PRECISION;
	}

	// --- Internal fee functions ---

	// Update the last fee operation time only if time passed >= decay interval. This prevents base rate griefing.
	function _updateLastFeeOpTime() internal {
		uint256 timePassed = block.timestamp - lastFeeOperationTime;

		if (timePassed >= SECONDS_IN_ONE_MINUTE) {
			lastFeeOperationTime = block.timestamp;
			emit LastFeeOpTimeUpdated(block.timestamp);
		}
	}

	function _calcDecayedBaseRate() internal view returns (uint256) {
		uint256 minutesPassed = (block.timestamp - lastFeeOperationTime) / SECONDS_IN_ONE_MINUTE;
		uint256 decayFactor = ZebraMath._decPow(minuteDecayFactor, minutesPassed);

		return (baseRate * decayFactor) / DECIMAL_PRECISION;
	}

	// --- Redemption functions ---

	/* Send _debtAmount debt to the system and redeem the corresponding amount of collateral from as many Troves as are needed to fill the redemption
	 * request.  Applies pending rewards to a Trove before reducing its debt and coll.
	 *
	 * Note that if _amount is very large, this function can run out of gas, specially if traversed troves are small. This can be easily avoided by
	 * splitting the total _amount in appropriate chunks and calling the function multiple times.
	 *
	 * Param `_maxIterations` can also be provided, so the loop through Troves is capped (if its zero, it will be ignored).This makes it easier to
	 * avoid OOG for the frontend, as only knowing approximately the average cost of an iteration is enough, without needing to know the topology
	 * of the trove list. It also avoids the need to set the cap in stone in the contract, nor doing gas calculations, as both gas price and opcode
	 * costs can vary.
	 *
	 * All Troves that are redeemed from -- with the likely exception of the last one -- will end up with no debt left, therefore they will be closed.
	 * If the last Trove does have some remaining debt, it has a finite ICR, and the reinsertion could be anywhere in the list, therefore it requires a hint.
	 * A frontend should use getRedemptionHints() to calculate what the ICR of this Trove will be after redemption, and pass a hint for its position
	 * in the sortedTroves list along with the ICR value that the hint was found for.
	 *
	 * If another transaction modifies the list between calling getRedemptionHints() and passing the hints to redeemCollateral(), it
	 * is very likely that the last (partially) redeemed Trove would end up with a different ICR than what the hint is for. In this case the
	 * redemption will stop after the last completely redeemed Trove and the sender will keep the remaining debt amount, which they can attempt
	 * to redeem later.
	 */
	function redeemCollateral(uint256 _debtAmount, address _firstRedemptionHint, address _upperPartialRedemptionHint, address _lowerPartialRedemptionHint, uint256 _partialRedemptionHintNICR, uint256 _maxIterations, uint256 _maxFeePercentage) external {
		ISortedTroves _sortedTrovesCached = sortedTroves;
		RedemptionTotals memory totals;

		require(_maxFeePercentage >= redemptionFeeFloor && _maxFeePercentage <= maxRedemptionFee, "Max fee 0.5% to 100%");
		require(block.timestamp >= systemDeploymentTime + BOOTSTRAP_PERIOD, "BOOTSTRAP_PERIOD");
		_distributeInterestDebt();
		totals.price = fetchPrice();
		uint256 _MCR = MCR;
		require(IBorrowerOperations(borrowerOperationsAddress).getTCR() >= _MCR, "Cannot redeem when TCR < MCR");
		require(_debtAmount > 0, "Amount must be greater than zero");
		require(ZebraUSD.balanceOf(msg.sender) >= _debtAmount, "Insufficient balance");
		totals.totalDebtSupplyAtStart = getGlobalSystemDebt();

		totals.remainingDebt = _debtAmount;
		address currentBorrower;

		if (_isValidFirstRedemptionHint(_sortedTrovesCached, _firstRedemptionHint, totals.price, _MCR)) {
			currentBorrower = _firstRedemptionHint;
		} else {
			currentBorrower = _sortedTrovesCached.getLast();
			// Find the first trove with ICR >= MCR
			while (currentBorrower != address(0) && getRedemptionICR(currentBorrower, totals.price) < _MCR) {
				currentBorrower = _sortedTrovesCached.getPrev(currentBorrower);
			}
		}

		// Loop through the Troves starting from the one with lowest collateral ratio until _amount of debt is exchanged for collateral
		if (_maxIterations == 0) {
			_maxIterations = type(uint256).max;
		}
		while (currentBorrower != address(0) && totals.remainingDebt > 0 && _maxIterations > 0) {
			_maxIterations--;
			// Save the address of the Trove preceding the current one, before potentially modifying the list
			address nextUserToCheck = _sortedTrovesCached.getPrev(currentBorrower);

			_applyPendingRewards(currentBorrower);
			SingleRedemptionValues memory singleRedemption = _redeemCollateralFromTrove(_sortedTrovesCached, currentBorrower, totals.remainingDebt, totals.price, _upperPartialRedemptionHint, _lowerPartialRedemptionHint, _partialRedemptionHintNICR);

			if (singleRedemption.cancelledPartial) break; // Partial redemption was cancelled (out-of-date hint, or new net debt < minimum), therefore we could not redeem from the last Trove

			totals.totalDebtToRedeem = totals.totalDebtToRedeem + singleRedemption.debtLot;
			totals.totalCollateralDrawn = totals.totalCollateralDrawn + singleRedemption.collateralLot;
			totals.totalInterest = totals.totalInterest + singleRedemption.interestLot;

			totals.remainingDebt = totals.remainingDebt - singleRedemption.debtLot;
			currentBorrower = nextUserToCheck;
		}
		require(totals.totalCollateralDrawn > 0, "Unable to redeem any amount");

		// Decay the baseRate due to time passed, and then increase it according to the size of this redemption.
		// Use the saved total debt supply value, from before it was reduced by the redemption.
		_updateBaseRateFromRedemption(totals.totalCollateralDrawn, totals.price, totals.totalDebtSupplyAtStart);
		// Calculate the collateral fee
		totals.collateralFee = sunsetting ? 0 : _calcRedemptionFee(getRedemptionRate(), totals.totalCollateralDrawn);
		_requireUserAcceptsFee(totals.collateralFee, totals.totalCollateralDrawn, _maxFeePercentage);

		_sendCollateral(feeReceiver(), totals.collateralFee);

		totals.collateralToSendToRedeemer = totals.totalCollateralDrawn - totals.collateralFee;

		emit Redemption(_debtAmount, totals.totalDebtToRedeem, totals.totalCollateralDrawn, totals.totalInterest, totals.collateralFee);

		// Burn the total debt that is cancelled with debt, and send the redeemed collateral to msg.sender
		ZebraUSD.burn(msg.sender, totals.totalDebtToRedeem);
		// Update Trove Manager debt, and send collateral to account
		totalActiveDebt = totalActiveDebt - totals.totalDebtToRedeem;
		decreaseOutstandingInterestDebt(totals.totalInterest);
		_sendCollateral(msg.sender, totals.collateralToSendToRedeemer);
		_resetState();
	}

	// Redeem as much collateral as possible from _borrower's Trove in exchange for debt up to _maxDebtAmount
	function _redeemCollateralFromTrove(
		ISortedTroves _sortedTrovesCached,
		address _borrower,
		uint256 _maxDebtAmount,
		uint256 _price,
		address _upperPartialRedemptionHint,
		address _lowerPartialRedemptionHint,
		uint256 _partialRedemptionHintNICR
	) internal returns (SingleRedemptionValues memory singleRedemption) {
		Trove storage t = Troves[_borrower];
		uint256 interest = getTroveInterest(_borrower, t.debt);
		if (_maxDebtAmount < interest) {
			singleRedemption.cancelledPartial = true;
			return singleRedemption;
		}
		singleRedemption.interestLot = interest;
		// Determine the remaining amount (lot) to be redeemed, capped by the entire debt of the Trove minus the liquidation reserve
		singleRedemption.debtLot = ZebraMath._min(_maxDebtAmount - singleRedemption.interestLot, t.debt - DEBT_GAS_COMPENSATION);

		// Get the CollateralLot of equivalent value in USD
		singleRedemption.collateralLot = ((singleRedemption.debtLot + singleRedemption.interestLot) * DECIMAL_PRECISION) / _price;
		// Decrease the debt and collateral of the current Trove according to the debt lot and corresponding collateral to send
		uint256 newDebt = (t.debt) - singleRedemption.debtLot;
		uint256 newColl = (t.coll) - singleRedemption.collateralLot;

		if (newDebt == DEBT_GAS_COMPENSATION) {
			// No debt left in the Trove (except for the liquidation reserve), therefore the trove gets closed
			_removeStake(_borrower);
			_closeTrove(_borrower, Status.closedByRedemption);
			_redeemCloseTrove(_borrower, DEBT_GAS_COMPENSATION, newColl);
			emit TroveUpdated(_borrower, 0, 0, 0, TroveManagerOperation.redeemCollateral);
		} else {
			uint256 newNICR = ZebraMath._computeNominalCR(newColl, newDebt);
			/*
			 * If the provided hint is out of date, we bail since trying to reinsert without a good hint will almost
			 * certainly result in running out of gas.
			 *
			 * If the resultant net debt of the partial is less than the minimum, net debt we bail.
			 */

			{
				// We check if the ICR hint is reasonable up to date, with continuous interest there might be slight differences (<1bps)
				uint256 icrError = _partialRedemptionHintNICR > newNICR ? _partialRedemptionHintNICR - newNICR : newNICR - _partialRedemptionHintNICR;
				if (icrError > 5e14 || _getNetDebt(newDebt) < IBorrowerOperations(borrowerOperationsAddress).minNetDebt()) {
					singleRedemption.cancelledPartial = true;
					return singleRedemption;
				}
			}

			_sortedTrovesCached.reInsert(_borrower, newNICR, _upperPartialRedemptionHint, _lowerPartialRedemptionHint);

			t.debt = newDebt;
			t.coll = newColl;
			_updateStakeAndTotalStakes(t);
			_updateTroveRewardSnapshots(_borrower);
			emit TroveUpdated(_borrower, newDebt, newColl, t.stake, TroveManagerOperation.redeemCollateral);
		}

		return singleRedemption;
	}

	/*
	 * Called when a full redemption occurs, and closes the trove.
	 * The redeemer swaps (debt - liquidation reserve) debt for (debt - liquidation reserve) worth of collateral, so the debt liquidation reserve left corresponds to the remaining debt.
	 * In order to close the trove, the debt liquidation reserve is burned, and the corresponding debt is removed.
	 * The debt recorded on the trove's struct is zero'd elswhere, in _closeTrove.
	 * Any surplus collateral left in the trove can be later claimed by the borrower.
	 */
	function _redeemCloseTrove(address _borrower, uint256 _debt, uint256 _collateral) internal {
		ZebraUSD.burn(gasPoolAddress, _debt);
		totalActiveDebt = totalActiveDebt - _debt;

		surplusBalances[_borrower] += _collateral;
		totalActiveCollateral -= _collateral;
	}

	function _isValidFirstRedemptionHint(ISortedTroves _sortedTroves, address _firstRedemptionHint, uint256 _price, uint256 _MCR) internal view returns (bool) {
		if (_firstRedemptionHint == address(0) || !_sortedTroves.contains(_firstRedemptionHint) || getRedemptionICR(_firstRedemptionHint, _price) < _MCR) {
			return false;
		}

		address nextTrove = _sortedTroves.getNext(_firstRedemptionHint);
		return nextTrove == address(0) || getRedemptionICR(nextTrove, _price) < _MCR;
	}

	/**
	 * Claim remaining collateral from a redemption or from a liquidation with ICR > MCR in Recovery Mode
	 */
	function claimCollateral(address _receiver) external {
		uint256 claimableColl = surplusBalances[msg.sender];
		require(claimableColl > 0, "No collateral available to claim");

		surplusBalances[msg.sender] = 0;

		collateralToken.safeTransfer(_receiver, claimableColl);
	}

	// --- Trove Adjustment functions ---

	function openTrove(address _borrower, uint256 _collateralAmount, uint256 _compositeDebt, uint256 NICR, address _upperHint, address _lowerHint) external whenNotPaused returns (uint256 stake, uint256 arrayIndex) {
		_requireCallerIsBO();
		require(!sunsetting, "Cannot open while sunsetting");
		uint256 supply = totalActiveDebt;
		Trove storage t = Troves[_borrower];
		require(t.status != Status.active, "BorrowerOps: Trove is active");
		t.status = Status.active;
		t.coll = _collateralAmount;
		t.debt = _compositeDebt;
		_updateTroveRewardSnapshots(_borrower);
		stake = _updateStakeAndTotalStakes(t);
		sortedTroves.insert(_borrower, NICR, _upperHint, _lowerHint);

		TroveOwners.push(_borrower);
		arrayIndex = TroveOwners.length - 1;
		t.arrayIndex = uint128(arrayIndex);

		totalActiveCollateral = totalActiveCollateral + _collateralAmount;
		uint256 _newTotalDebt = supply + _compositeDebt;
		require(_newTotalDebt + defaultedDebt <= maxSystemDebt, "Collateral debt limit reached");
		totalActiveDebt = _newTotalDebt;
	}

	function updateTroveFromAdjustment(bool _isDebtIncrease, uint256 _debtChange, uint256 _netDebtChange, bool _isCollIncrease, uint256 _collChange, address _upperHint, address _lowerHint, address _borrower, address _receiver) external returns (uint256, uint256, uint256) {
		_requireCallerIsBO();
		if (_isCollIncrease || _isDebtIncrease) {
			require(!paused, "Collateral Paused");
			require(!sunsetting, "Cannot increase while sunsetting");
		}

		Trove storage t = Troves[_borrower];
		require(t.status == Status.active, "Trove closed or does not exist");

		uint256 newDebt = t.debt;
		if (_debtChange > 0) {
			if (_isDebtIncrease) {
				newDebt = newDebt + _netDebtChange;
				_increaseDebt(_receiver, _netDebtChange, _debtChange);
			} else {
				newDebt = newDebt - _netDebtChange;
				_decreaseDebt(_receiver, _debtChange);
			}
			t.debt = newDebt;
		}

		uint256 newColl = t.coll;
		if (_collChange > 0) {
			if (_isCollIncrease) {
				newColl = newColl + _collChange;
				totalActiveCollateral = totalActiveCollateral + _collChange;
				// trust that BorrowerOperations sent the collateral
			} else {
				newColl = newColl - _collChange;
				_sendCollateral(_receiver, _collChange);
			}
			t.coll = newColl;
		}

		uint256 newNICR = ZebraMath._computeNominalCR(newColl, newDebt);
		sortedTroves.reInsert(_borrower, newNICR, _upperHint, _lowerHint);

		return (newColl, newDebt, _updateStakeAndTotalStakes(t));
	}

	function closeTrove(address _borrower, address _receiver, uint256 collAmount, uint256 debtAmount) external {
		_requireCallerIsBO();
		require(Troves[_borrower].status == Status.active, "Trove closed or does not exist");
		_removeStake(_borrower);
		_closeTrove(_borrower, Status.closedByOwner);
		totalActiveDebt = totalActiveDebt - debtAmount;
		_sendCollateral(_receiver, collAmount);
		_resetState();
	}

	/**
        @dev Only called from `closeTrove` because liquidating the final trove is blocked in
             `LiquidationManager`. Many liquidation paths involve redistributing debt and
             collateral to existing troves. If the collateral is being sunset, the final trove
             must be closed by repaying the debt or via a redemption.
     */
	function _resetState() private {
		if (TroveOwners.length == 0) {
			lastInterestDebtUpdateTime = 0;
			totalStakes = 0;
			totalStakesSnapshot = 0;
			totalCollateralSnapshot = 0;
			L_collateral = 0;
			L_debt = 0;
			L_Interest_Debt = 0;
			lastInterestDebtError_Redistribution = 0;
			lastCollateralError_Redistribution = 0;
			lastDebtError_Redistribution = 0;
			totalActiveCollateral = 0;
			totalActiveDebt = 0;
			defaultedCollateral = 0;
			defaultedDebt = 0;
			outstandingInterestDebt = 0;
		}
	}

	function _closeTrove(address _borrower, Status closedStatus) internal {
		uint256 TroveOwnersArrayLength = TroveOwners.length;

		Trove storage t = Troves[_borrower];
		t.status = closedStatus;
		t.coll = 0;
		t.debt = 0;
		ISortedTroves sortedTrovesCached = sortedTroves;
		rewardSnapshots[_borrower].collateral = 0;
		rewardSnapshots[_borrower].debt = 0;
		if (TroveOwnersArrayLength > 1 && sortedTrovesCached.getSize() > 1) {
			// remove trove owner from the TroveOwners array, not preserving array order
			uint128 index = t.arrayIndex;
			address addressToMove = TroveOwners[TroveOwnersArrayLength - 1];
			TroveOwners[index] = addressToMove;
			Troves[addressToMove].arrayIndex = index;
			emit TroveIndexUpdated(addressToMove, index);
		}

		TroveOwners.pop();

		sortedTrovesCached.remove(_borrower);
		t.arrayIndex = 0;
	}

	// Updates the baseRate state variable based on time elapsed since the last redemption or debt borrowing operation.
	function decayBaseRateAndGetBorrowingFee(uint256 _debt) external returns (uint256) {
		_requireCallerIsBO();
		uint256 rate = _decayBaseRate();

		return _calcBorrowingFee(_calcBorrowingRate(rate), _debt);
	}

	function _decayBaseRate() internal returns (uint256) {
		uint256 decayedBaseRate = _calcDecayedBaseRate();

		baseRate = decayedBaseRate;
		emit BaseRateUpdated(decayedBaseRate);

		_updateLastFeeOpTime();

		return decayedBaseRate;
	}

	function applyPendingRewards(address _borrower) external returns (uint256 coll, uint256 debt) {
		_requireCallerIsBO();
		return _applyPendingRewards(_borrower);
	}

	// Add the borrowers's coll and debt rewards earned from redistributions, to their Trove
	function _applyPendingRewards(address _borrower) internal returns (uint256, uint256) {
		Trove storage t = Troves[_borrower];
		if (t.status == Status.active) {
			(uint256 pendingCollateralReward, uint256 pendingDebtReward) = getPendingCollAndDebtRewards(_borrower);
			// Apply pending rewards to trove's state
			t.coll += pendingCollateralReward;
			t.debt += pendingDebtReward;

			_updateTroveRewardSnapshots(_borrower);

			_movePendingTroveRewardsToActiveBalance(pendingDebtReward, pendingCollateralReward);

			emit TroveUpdated(_borrower, t.debt, t.coll, t.stake, TroveManagerOperation.applyPendingRewards);
		}
		return (t.coll, t.debt);
	}

	function _updateTroveRewardSnapshots(address _borrower) internal {
		uint256 L_collateralCached = L_collateral;
		uint256 L_debtCached = L_debt;
		uint256 L_InterestDebtCached = L_Interest_Debt;
		rewardSnapshots[_borrower] = RewardSnapshot(L_collateralCached, L_debtCached, L_InterestDebtCached);
		emit TroveSnapshotsUpdated(L_collateralCached, L_debtCached, L_InterestDebtCached);
	}

	function repayInterestDebt(address _borrower) external {
		_distributeInterestDebt();
		_applyPendingRewards(_borrower);
		(uint256 debt, , , ) = getEntireDebtAndColl(_borrower);
		_repayInterest(msg.sender, _borrower, debt);
	}

	function repayInterest(address _account, address _borrower, uint256 _debt) public {
		_requireCallerIsBO();
		_repayInterest(_account, _borrower, _debt);
	}

	function _repayInterest(address _account, address _borrower, uint256 _debt) internal {
		uint256 interest = getTroveInterest(_borrower, _debt);
		if (ZebraUSD.balanceOf(_account) >= interest) {
			ZebraUSD.burn(_account, interest);
		} else {
			totalActiveDebt += interest;
			Troves[_borrower].debt += interest;
		}
		decreaseOutstandingInterestDebt(interest);
		_updateTroveRewardSnapshots(_borrower);
		emit InsterstPaid(_account, _borrower, interest);
	}

	// Remove borrower's stake from the totalStakes sum, and set their stake to 0
	function _removeStake(address _borrower) internal {
		uint256 stake = Troves[_borrower].stake;
		totalStakes = totalStakes - stake;
		Troves[_borrower].stake = 0;
	}

	// Update borrower's stake based on their latest collateral value
	function _updateStakeAndTotalStakes(Trove storage t) internal returns (uint256) {
		uint256 newStake = _computeNewStake(t.coll);
		uint256 oldStake = t.stake;
		t.stake = newStake;
		uint256 newTotalStakes = totalStakes - oldStake + newStake;
		totalStakes = newTotalStakes;
		emit TotalStakesUpdated(newTotalStakes);

		return newStake;
	}

	// Calculate a new stake based on the snapshots of the totalStakes and totalCollateral taken at the last liquidation
	function _computeNewStake(uint256 _coll) internal view returns (uint256) {
		uint256 stake;
		uint256 totalCollateralSnapshotCached = totalCollateralSnapshot;
		if (totalCollateralSnapshotCached == 0) {
			stake = _coll;
		} else {
			/*
			 * The following assert() holds true because:
			 * - The system always contains >= 1 trove
			 * - When we close or liquidate a trove, we redistribute the pending rewards, so if all troves were closed/liquidated,
			 * rewards wouldve been emptied and totalCollateralSnapshot would be zero too.
			 */
			uint256 totalStakesSnapshotCached = totalStakesSnapshot;
			assert(totalStakesSnapshotCached > 0);
			stake = (_coll * totalStakesSnapshotCached) / totalCollateralSnapshotCached;
		}
		return stake;
	}

	function closeLastTroveWhenSunsetting() external onlyOwner {
		require(sunsetting, "Not in sunsetting");
		require(TroveOwners.length == 1, "Can only force to close last trove");
		address _borrower = TroveOwners[0];
		uint256 ICR = getCurrentICR(_borrower, fetchPrice());
		uint256 TCR = IBorrowerOperations(borrowerOperationsAddress).getTCR();
		require(ICR < 1e18 || TCR < MCR, "Can only force close bad borrower");
		(uint256 coll, uint256 debt) = getTroveCollAndDebt(_borrower);
		uint256 interest = getTroveInterest(_borrower, debt);
		totalActiveDebt = totalActiveDebt - debt;
		totalActiveCollateral = totalActiveCollateral - coll;
		ZebraUSD.burn(msg.sender, debt + interest);
		_sendCollateral(msg.sender, coll);
		_removeStake(_borrower);
		_closeTrove(_borrower, Status.closedByOwner);
		_resetState();
	}

	// --- Liquidation Functions ---

	function closeTroveByLiquidation(address _borrower) external {
		_requireCallerIsLM();
		_removeStake(_borrower);
		_closeTrove(_borrower, Status.closedByLiquidation);
	}

	function movePendingTroveRewardsToActiveBalances(uint256 _debt, uint256 _collateral) external {
		_requireCallerIsLM();
		_movePendingTroveRewardsToActiveBalance(_debt, _collateral);
	}

	function _movePendingTroveRewardsToActiveBalance(uint256 _debt, uint256 _collateral) internal {
		defaultedDebt -= _debt;
		totalActiveDebt += _debt;
		defaultedCollateral -= _collateral;
		totalActiveCollateral += _collateral;
	}

	function addCollateralSurplus(address borrower, uint256 collSurplus) external {
		_requireCallerIsLM();
		surplusBalances[borrower] += collSurplus;
	}

	function finalizeLiquidation(address _liquidator, uint256 _debt, uint256 _coll, uint256 _collSurplus, uint256 _debtGasComp, uint256 _collGasComp, uint256 _interest) external {
		_requireCallerIsLM();
		// redistribute debt and collateral
		_redistributeDebtAndColl(_debt, _coll);

		uint256 _activeColl = totalActiveCollateral;
		if (_collSurplus > 0) {
			_activeColl -= _collSurplus;
			totalActiveCollateral = _activeColl;
		}

		// update system snapshos
		totalStakesSnapshot = totalStakes;
		totalCollateralSnapshot = _activeColl - _collGasComp + defaultedCollateral;
		emit SystemSnapshotsUpdated(totalStakesSnapshot, totalCollateralSnapshot);
		decreaseOutstandingInterestDebt(_interest);
		// send gas compensation
		ZebraUSD.returnFromPool(gasPoolAddress, _liquidator, _debtGasComp);
		_sendCollateral(_liquidator, _collGasComp);
	}

	function _redistributeDebtAndColl(uint256 _debt, uint256 _coll) internal {
		if (_debt == 0) {
			return;
		}
		/*
		 * Add distributed coll and debt rewards-per-unit-staked to the running totals. Division uses a "feedback"
		 * error correction, to keep the cumulative error low in the running totals L_collateral and L_debt:
		 *
		 * 1) Form numerators which compensate for the floor division errors that occurred the last time this
		 * function was called.
		 * 2) Calculate "per-unit-staked" ratios.
		 * 3) Multiply each ratio back by its denominator, to reveal the current floor division error.
		 * 4) Store these errors for use in the next correction when this function is called.
		 * 5) Note: static analysis tools complain about this "division before multiplication", however, it is intended.
		 */
		uint256 collateralNumerator = (_coll * DECIMAL_PRECISION) + lastCollateralError_Redistribution;
		uint256 debtNumerator = (_debt * DECIMAL_PRECISION) + lastDebtError_Redistribution;
		uint256 totalStakesCached = totalStakes;
		// Get the per-unit-staked terms
		uint256 collateralRewardPerUnitStaked = collateralNumerator / totalStakesCached;
		uint256 debtRewardPerUnitStaked = debtNumerator / totalStakesCached;

		lastCollateralError_Redistribution = collateralNumerator - (collateralRewardPerUnitStaked * totalStakesCached);
		lastDebtError_Redistribution = debtNumerator - (debtRewardPerUnitStaked * totalStakesCached);

		// Add per-unit-staked terms to the running totals
		uint256 new_L_collateral = L_collateral + collateralRewardPerUnitStaked;
		uint256 new_L_debt = L_debt + debtRewardPerUnitStaked;
		L_collateral = new_L_collateral;
		L_debt = new_L_debt;

		emit LTermsUpdated(new_L_collateral, new_L_debt);

		totalActiveDebt -= _debt;
		defaultedDebt += _debt;
		defaultedCollateral += _coll;
		totalActiveCollateral -= _coll;
	}

	// --- Trove property setters ---

	function _sendCollateral(address _account, uint256 _amount) private {
		if (_amount > 0) {
			totalActiveCollateral = totalActiveCollateral - _amount;
			emit CollateralSent(_account, _amount);

			collateralToken.safeTransfer(_account, _amount);
		}
	}

	function _increaseDebt(address account, uint256 netDebtAmount, uint256 debtAmount) internal {
		uint256 _newTotalDebt = totalActiveDebt + netDebtAmount;
		require(_newTotalDebt + defaultedDebt <= maxSystemDebt, "Collateral debt limit reached");
		totalActiveDebt = _newTotalDebt;
		ZebraUSD.mint(account, debtAmount);
	}

	function decreaseDebtAndSendCollateral(address account, uint256 debt, uint256 coll) external {
		_requireCallerIsLM();
		_decreaseDebt(account, debt);
		_sendCollateral(account, coll);
	}

	function _decreaseDebt(address account, uint256 amount) internal {
		ZebraUSD.burn(account, amount);
		totalActiveDebt = totalActiveDebt - amount;
	}

	// --- Requires ---

	function _requireCallerIsBO() internal view {
		require(msg.sender == borrowerOperationsAddress, "Caller not BO");
	}

	function _requireCallerIsLM() internal view {
		require(msg.sender == liquidationManager, "Not Liquidation Manager");
	}
}

