// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../dependencies/ZebraOwnableUpgradeable.sol";
import "../interfaces/IZebra.sol";
import "../interfaces/IEsZebra.sol";

contract Vest is ZebraOwnableUpgradeable {
	enum State {
		inCliff,
		inRelease,
		outOfRelease
	}

	struct OrderInfo {
		uint64 start;
		uint64 cliffEnd;
		uint64 releaseEnd;
		uint64 released;
		uint256 amount;
	}

	IEsZebra public esZebra;
	IZebra public Zebra;

	uint64 public cliffTime;
	uint64 public releaseTime;
	uint64 public minDeposits;

	mapping(address => uint256) public currentIds;
	mapping(address => mapping(uint256 => OrderInfo)) public orderInfos;

	event Staked(address account, uint256 id, uint256 amount);
	event Claimed(address account, uint256 id, uint256 amount);

	function initialize(IZebraCore _ZebraCore, IZebra _Zebra, IEsZebra _esZebra) external initializer {
		__InitCore(_ZebraCore);
		Zebra = _Zebra;
		esZebra = _esZebra;
		cliffTime = 14 days;
		releaseTime = 14 days;
		minDeposits = 1e18;
	}

	function stake(uint256 amount) external {
		require(amount >= minDeposits, "Vest: too little deposits to stake");
		address account = msg.sender;
		esZebra.sendToken(account, amount);
		uint256 currentId = currentIds[account]++;
		orderInfos[account][currentId] = OrderInfo({ start: uint64(block.timestamp), cliffEnd: uint64(block.timestamp + cliffTime), releaseEnd: uint64(block.timestamp + cliffTime + releaseTime), released: 0, amount: amount });
		emit Staked(account, currentId, amount);
	}

	function stateOf(address account, uint256 id) public view returns (State) {
		require(id < currentIds[account], "Vest: invalid order id");
		if (block.timestamp < orderInfos[account][id].cliffEnd) {
			return State.inCliff;
		} else if (block.timestamp < orderInfos[account][id].releaseEnd) {
			return State.inRelease;
		}
		return State.outOfRelease;
	}

	function claimAll(uint256[] memory ids) external {
		address account = msg.sender;
		for (uint256 i = 0; i < ids.length; i++) {
			uint256 id = ids[i];
			require(id < currentIds[account], "Vest: invalid id");
			State state = stateOf(account, id);
			uint64 total = orderInfos[account][id].releaseEnd - orderInfos[account][id].cliffEnd;
			if (orderInfos[account][id].released == total) {
				continue;
			}
			if (state == State.inCliff) {
				continue;
			} else if (state == State.inRelease) {
				uint64 walked = uint64(block.timestamp - orderInfos[account][id].cliffEnd);
				uint256 amount = ((walked - orderInfos[account][id].released) * orderInfos[account][id].amount) / total;
				orderInfos[account][id].released = walked;
				Zebra.esZebra2Zebra(account, amount);
				emit Claimed(account, id, amount);
			} else {
				uint64 leftWalk = total - orderInfos[account][id].released;
				uint256 amount = (leftWalk * orderInfos[account][id].amount) / total;
				orderInfos[account][id].released = total;
				Zebra.esZebra2Zebra(account, amount);
				emit Claimed(account, id, amount);
			}
		}
	}

	function claim(uint256 id) external {
		address account = msg.sender;
		require(id < currentIds[account], "Vest: invalid id");
		State state = stateOf(account, id);
		uint64 total = orderInfos[account][id].releaseEnd - orderInfos[account][id].cliffEnd;
		require(orderInfos[account][id].released < total, "Vest: order claimed");
		if (state == State.inCliff) {
			revert("Vest: in cliff");
		} else if (state == State.inRelease) {
			uint64 walked = uint64(block.timestamp - orderInfos[account][id].cliffEnd);
			uint256 amount = ((walked - orderInfos[account][id].released) * orderInfos[account][id].amount) / total;
			orderInfos[account][id].released = walked;
			Zebra.esZebra2Zebra(account, amount);
			emit Claimed(account, id, amount);
		} else {
			uint64 leftWalk = total - orderInfos[account][id].released;
			uint256 amount = (leftWalk * orderInfos[account][id].amount) / total;
			orderInfos[account][id].released = total;
			Zebra.esZebra2Zebra(account, amount);
			emit Claimed(account, id, amount);
		}
	}

	function earned(address account, uint256 id) public view returns (uint256) {
		require(id < currentIds[account], "Vest: invalid id");
		State state = stateOf(account, id);
		uint64 total = orderInfos[account][id].releaseEnd - orderInfos[account][id].cliffEnd;
		if (total == orderInfos[account][id].released) {
			return 0;
		}
		if (state == State.inCliff) {
			return 0;
		} else if (state == State.inRelease) {
			uint64 walked = uint64(block.timestamp - orderInfos[account][id].cliffEnd);
			return ((walked - orderInfos[account][id].released) * orderInfos[account][id].amount) / total;
		} else {
			uint64 leftWalk = total - orderInfos[account][id].released;
			return (leftWalk * orderInfos[account][id].amount) / total;
		}
	}

	function batchEarned(address account, uint256[] memory ids) public view returns (uint256[] memory earneds) {
		earneds = new uint256[](ids.length);
		for (uint256 i = 0; i < ids.length; i++) {
			earneds[i] = earned(account, ids[i]);
		}
	}

	function totalEarned(address account, uint256[] memory ids) public view returns (uint256 total) {
		for (uint256 i = 0; i < ids.length; i++) {
			total += earned(account, ids[i]);
		}
	}
}

contracts/core/Zebra.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";

import "../interfaces/IZebra.sol";
import "../interfaces/IEsZebra.sol";
import "../dependencies/ZebraOwnable.sol";

contract Zebra is ERC20Burnable, ERC20Permit, ZebraOwnable, IZebra {
	IEsZebra public esZebra;
	address public vest;

	uint256 public immutable MaxCap = 1000000000 ether;

	constructor(IZebraCore _zebraCore, IEsZebra _esZebra, address _vest) ERC20("Zebra Coin", "Zebra") ERC20Permit("Zebra Coin") ZebraOwnable(_zebraCore) {
		esZebra = _esZebra;
		vest = _vest;
	}

	function mint(address account, uint256 amount) external onlyOwner {
		require(totalSupply() + esZebra.totalSupply() + amount <= MaxCap, "Zebra: exceeds MaxCap");
		_mint(account, amount);
	}

	function mintEsZebra(address account, uint256 amount) external onlyOwner {
		require(totalSupply() + esZebra.totalSupply() + amount <= MaxCap, "Zebra: exceeds MaxCap");
		esZebra.mint(account, amount);
	}

	function zebra2EsZebra(address account, uint256 amount) external override {
		_burn(msg.sender, amount);
		esZebra.mint(account, amount);
		emit ZebraToEsZebra(account, amount);
	}

	function esZebra2Zebra(address account, uint256 amount) external override {
		require(msg.sender == vest, "Zebra: Caller is not Vest");
		esZebra.burnFromZebra(msg.sender, amount);
		_mint(account, amount);
		emit EsZebraToZebra(account, amount);
	}

	function burn(uint256 amount) public override(ERC20Burnable, IZebra) {
		ERC20Burnable.burn(amount);
	}

	function burnFrom(address account, uint256 amount) public override(ERC20Burnable, IZebra) {
		ERC20Burnable.burnFrom(account, amount);
	}
}

contracts/core/ZebraCore.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

/**
    @title Zebra ZebraCore
    @notice Single source of truth for system-wide values and contract ownership.

            Ownership of this contract should be the Zebra DAO via `AdminVoting`.
            Other ownable Zebra contracts inherit their ownership from this contract
            using `ZebraOwnable`.
 */
contract ZebraCore {
	address public feeReceiver;
	address public priceFeed;

	address public owner;
	address public pendingOwner;
	uint256 public ownershipTransferDeadline;

	address public guardian;

	// We enforce a three day delay between committing and applying
	// an ownership change, as a sanity check on a proposed new owner
	// and to give users time to react in case the act is malicious.
	uint256 public constant OWNERSHIP_TRANSFER_DELAY = 86400 * 3;

	// System-wide pause. When true, disables trove adjustments across all collaterals.
	bool public paused;

	// System-wide start time, rounded down the nearest epoch week.
	// Other contracts that require access to this should inherit `SystemStart`.
	uint256 public immutable startTime;

	event NewOwnerCommitted(address owner, address pendingOwner, uint256 deadline);

	event NewOwnerAccepted(address oldOwner, address owner);

	event NewOwnerRevoked(address owner, address revokedOwner);

	event FeeReceiverSet(address feeReceiver);

	event GuardianSet(address guardian);

	event Paused();

	event Unpaused();

	constructor(address _owner, address _guardian, address _feeReceiver) {
		owner = _owner;
		startTime = (block.timestamp / 1 weeks) * 1 weeks;
		guardian = _guardian;
		feeReceiver = _feeReceiver;
		emit GuardianSet(_guardian);
		emit FeeReceiverSet(_feeReceiver);
	}

	modifier onlyOwner() {
		require(msg.sender == owner, "Only owner");
		_;
	}

	/**
	 * @notice Set the receiver of all fees across the protocol
	 * @param _feeReceiver Address of the fee's recipient
	 */
	function setFeeReceiver(address _feeReceiver) external onlyOwner {
		feeReceiver = _feeReceiver;
		emit FeeReceiverSet(_feeReceiver);
	}

	/**
     * @notice Set the guardian address
               The guardian can execute some emergency actions
     * @param _guardian Guardian address
     */
	function setGuardian(address _guardian) external onlyOwner {
		guardian = _guardian;
		emit GuardianSet(_guardian);
	}

	/**
	 * @notice Sets the global pause state of the protocol
	 *         Pausing is used to mitigate risks in exceptional circumstances
	 *         Functionalities affected by pausing are:
	 *         - New borrowing is not possible
	 *         - New collateral deposits are not possible
	 *         - New stability pool deposits are not possible
	 * @param _paused If true the protocol is paused
	 */
	function setPaused(bool _paused) external {
		require((_paused && msg.sender == guardian) || msg.sender == owner, "Unauthorized");
		paused = _paused;
		if (_paused) {
			emit Paused();
		} else {
			emit Unpaused();
		}
	}

	function commitTransferOwnership(address newOwner) external onlyOwner {
		pendingOwner = newOwner;
		ownershipTransferDeadline = block.timestamp + OWNERSHIP_TRANSFER_DELAY;

		emit NewOwnerCommitted(msg.sender, newOwner, block.timestamp + OWNERSHIP_TRANSFER_DELAY);
	}

	function acceptTransferOwnership() external {
		require(msg.sender == pendingOwner, "Only new owner");
		require(block.timestamp >= ownershipTransferDeadline, "Deadline not passed");

		emit NewOwnerAccepted(owner, msg.sender);

		owner = pendingOwner;
		pendingOwner = address(0);
		ownershipTransferDeadline = 0;
	}

	function revokeTransferOwnership() external onlyOwner {
		emit NewOwnerRevoked(msg.sender, pendingOwner);

		pendingOwner = address(0);
		ownershipTransferDeadline = 0;
	}
}

contracts/core/ZebraUSD.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20FlashMint.sol";
import "../interfaces/IZebraCore.sol";

/**
    @title Zebra Debt Token "ZebraUSD"
    @notice CDP minted against collateral deposits within `TroveManager`.
            This contract has a 1:n relationship with multiple deployments of `TroveManager`,
            each of which hold one collateral type which may be used to mint this token.
 */
contract ZebraUSD is ERC20Burnable, ERC20Permit, ERC20FlashMint {
	// --- ERC 3156 Data ---
	uint256 public constant FLASH_LOAN_FEE = 9; // 1 = 0.0001%
	// Amount of debt to be locked in gas pool on opening troves
	uint256 public immutable DEBT_GAS_COMPENSATION;

	IZebraCore private immutable zebraCore;

	address public immutable stabilityPoolAddress;
	address public immutable borrowerOperationsAddress;
	address public immutable factory;
	address public immutable gasPool;

	mapping(address => bool) public troveManager;

	constructor(IZebraCore _zebraCore, address _stabilityPoolAddress, address _borrowerOperationsAddress, address _factory, address _gasPool, uint256 _gasCompensation) ERC20("Zebra USD", "zUSD") ERC20Permit("Zebra USD") {
		stabilityPoolAddress = _stabilityPoolAddress;
		zebraCore = _zebraCore;
		borrowerOperationsAddress = _borrowerOperationsAddress;
		factory = _factory;
		gasPool = _gasPool;

		DEBT_GAS_COMPENSATION = _gasCompensation;
	}

	function enableTroveManager(address _troveManager) external {
		require(msg.sender == factory, "!Factory");
		troveManager[_troveManager] = true;
	}

	// --- Functions for intra-Zebra calls ---

	function mintWithGasCompensation(address _account, uint256 _amount) external returns (bool) {
		require(msg.sender == borrowerOperationsAddress, "ZebraUSD: Caller not BO");
		_mint(_account, _amount);
		_mint(gasPool, DEBT_GAS_COMPENSATION);

		return true;
	}

	function burnWithGasCompensation(address _account, uint256 _amount) external returns (bool) {
		require(msg.sender == borrowerOperationsAddress, "ZebraUSD: Caller not BO");
		_burn(_account, _amount);
		_burn(gasPool, DEBT_GAS_COMPENSATION);
		return true;
	}

	function mint(address _account, uint256 _amount) external {
		require(msg.sender == borrowerOperationsAddress || troveManager[msg.sender], "ZebraUSD: Caller not BO/TM");
		_mint(_account, _amount);
	}

	function burn(address _account, uint256 _amount) external {
		require(troveManager[msg.sender], "ZebraUSD: Caller not TroveManager");
		_burn(_account, _amount);
	}

	function sendToSP(address _sender, uint256 _amount) external {
		require(msg.sender == stabilityPoolAddress, "ZebraUSD: Caller not StabilityPool");
		_transfer(_sender, msg.sender, _amount);
	}

	function returnFromPool(address _poolAddress, address _receiver, uint256 _amount) external {
		require(msg.sender == stabilityPoolAddress || troveManager[msg.sender], "ZebraUSD: Caller not TM/SP");
		_transfer(_poolAddress, _receiver, _amount);
	}

	// --- External functions ---

	function transfer(address recipient, uint256 amount) public override returns (bool) {
		_requireValidRecipient(recipient);
		return super.transfer(recipient, amount);
	}

	function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
		_requireValidRecipient(recipient);
		return super.transferFrom(sender, recipient, amount);
	}

	/**
	 * @dev Returns the fee applied when doing flash loans. This function calls
	 * the {_flashFee} function which returns the fee applied when doing flash
	 * loans.
	 * @param token The token to be flash loaned.
	 * @param amount The amount of tokens to be loaned.
	 * @return The fees applied to the corresponding flash loan.
	 */
	function flashFee(address token, uint256 amount) public view override returns (uint256) {
		return token == address(this) ? _flashFee(amount) : 0;
	}

	/**
	 * @dev Returns the fee applied when doing flash loans. By default this
	 * implementation has 0 fees. This function can be overloaded to make
	 * the flash loan mechanism deflationary.
	 * @param amount The amount of tokens to be loaned.
	 * @return The fees applied to the corresponding flash loan.
	 */
	function _flashFee(uint256 amount) internal pure returns (uint256) {
		return (amount * FLASH_LOAN_FEE) / 10000;
	}

	function _flashFeeReceiver() internal view override returns (address) {
		return zebraCore.feeReceiver();
	}

	// --- 'require' functions ---
	function _requireValidRecipient(address _recipient) internal view {
		require(_recipient != address(0) && _recipient != address(this), "ZebraUSD: Cannot transfer tokens directly to the Debt token contract or the zero address");
		require(_recipient != stabilityPoolAddress && !troveManager[_recipient] && _recipient != borrowerOperationsAddress, "ZebraUSD: Cannot transfer tokens directly to the StabilityPool, TroveManager or BorrowerOps");
	}
}

