contracts/helpers/MultiCollateralHintHel.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../interfaces/IBorrowerOperations.sol";
import "../interfaces/ITroveManager.sol";
import "../interfaces/ISortedTroves.sol";
import "../interfaces/IFactory.sol";
import "../dependencies/ZebraBase.sol";
import "../dependencies/ZebraMath.sol";

contract MultiCollateralHintHelpers is ZebraBase {
	IBorrowerOperations public immutable borrowerOperations;

	constructor(address _borrowerOperationsAddress, uint256 _gasCompensation) ZebraBase(_gasCompensation) {
		borrowerOperations = IBorrowerOperations(_borrowerOperationsAddress);
	}

	// --- Functions ---

	/* getRedemptionHints() - Helper function for finding the right hints to pass to redeemCollateral().
	 *
	 * It simulates a redemption of `_debtAmount` to figure out where the redemption sequence will start and what state the final Trove
	 * of the sequence will end up in.
	 *
	 * Returns three hints:
	 *  - `firstRedemptionHint` is the address of the first Trove with ICR >= MCR (i.e. the first Trove that will be redeemed).
	 *  - `partialRedemptionHintNICR` is the final nominal ICR of the last Trove of the sequence after being hit by partial redemption,
	 *     or zero in case of no partial redemption.
	 *  - `truncatedDebtAmount` is the maximum amount that can be redeemed out of the the provided `_debtAmount`. This can be lower than
	 *    `_debtAmount` when redeeming the full amount would leave the last Trove of the redemption sequence with less net debt than the
	 *    minimum allowed value (i.e. MIN_NET_DEBT).
	 *
	 * The number of Troves to consider for redemption can be capped by passing a non-zero value as `_maxIterations`, while passing zero
	 * will leave it uncapped.
	 */

	function getRedemptionHints(ITroveManager troveManager, uint256 _debtAmount, uint256 _price, uint256 _maxIterations) external view returns (address firstRedemptionHint, uint256 partialRedemptionHintNICR, uint256 truncatedDebtAmount) {
		ISortedTroves sortedTrovesCached = ISortedTroves(troveManager.sortedTroves());

		uint256 remainingDebt = _debtAmount;
		address currentTroveuser = sortedTrovesCached.getLast();
		uint256 MCR = troveManager.MCR();

		while (currentTroveuser != address(0) && troveManager.getRedemptionICR(currentTroveuser, _price) < MCR) {
			currentTroveuser = sortedTrovesCached.getPrev(currentTroveuser);
		}

		firstRedemptionHint = currentTroveuser;

		if (_maxIterations == 0) {
			_maxIterations = type(uint256).max;
		}

		uint256 minNetDebt = borrowerOperations.minNetDebt();
		while (currentTroveuser != address(0) && remainingDebt > 0 && _maxIterations-- > 0) {
			(uint256 debt, uint256 coll, , ) = troveManager.getEntireDebtAndColl(currentTroveuser);
			uint256 interest = troveManager.getTroveInterest(currentTroveuser, debt);
			if (remainingDebt < interest) {
				break;
			}
			uint256 netDebt = _getNetDebt(debt);
			if (netDebt > remainingDebt - interest) {
				if (netDebt > minNetDebt) {
					uint256 maxRedeemableDebt = ZebraMath._min(remainingDebt - interest, netDebt - minNetDebt);

					uint256 newColl = coll - (((maxRedeemableDebt + interest) * DECIMAL_PRECISION) / _price);
					uint256 newDebt = netDebt - maxRedeemableDebt;

					uint256 compositeDebt = _getCompositeDebt(newDebt);
					partialRedemptionHintNICR = ZebraMath._computeNominalCR(newColl, compositeDebt);

					remainingDebt = remainingDebt - maxRedeemableDebt;
				}
				break;
			} else {
				remainingDebt = remainingDebt - netDebt;
			}

			currentTroveuser = sortedTrovesCached.getPrev(currentTroveuser);
		}

		truncatedDebtAmount = _debtAmount - remainingDebt;
	}

	/* getApproxHint() - return address of a Trove that is, on average, (length / numTrials) positions away in the
    sortedTroves list from the correct insert position of the Trove to be inserted.

    Note: The output address is worst-case O(n) positions away from the correct insert position, however, the function
    is probabilistic. Input can be tuned to guarantee results to a high degree of confidence, e.g:

    Submitting numTrials = k * sqrt(length), with k = 15 makes it very, very likely that the ouput address will
    be <= sqrt(length) positions away from the correct insert position.
    */
	function getApproxHint(ITroveManager troveManager, uint256 _CR, uint256 _numTrials, uint256 _inputRandomSeed) external view returns (address hintAddress, uint256 diff, uint256 latestRandomSeed) {
		ISortedTroves sortedTroves = ISortedTroves(troveManager.sortedTroves());
		uint256 arrayLength = troveManager.getTroveOwnersCount();

		if (arrayLength == 0) {
			return (address(0), 0, _inputRandomSeed);
		}

		hintAddress = sortedTroves.getLast();
		diff = ZebraMath._getAbsoluteDifference(_CR, troveManager.getNominalICR(hintAddress));
		latestRandomSeed = _inputRandomSeed;

		uint256 i = 1;

		while (i < _numTrials) {
			latestRandomSeed = uint256(keccak256(abi.encodePacked(latestRandomSeed)));

			uint256 arrayIndex = latestRandomSeed % arrayLength;
			address currentAddress = troveManager.getTroveFromTroveOwnersArray(arrayIndex);
			uint256 currentNICR = troveManager.getNominalICR(currentAddress);

			// check if abs(current - CR) > abs(closest - CR), and update closest if current is closer
			uint256 currentDiff = ZebraMath._getAbsoluteDifference(currentNICR, _CR);

			if (currentDiff < diff) {
				diff = currentDiff;
				hintAddress = currentAddress;
			}
			i++;
		}
	}

	function computeNominalCR(uint256 _coll, uint256 _debt) external pure returns (uint256) {
		return ZebraMath._computeNominalCR(_coll, _debt);
	}

	function computeCR(uint256 _coll, uint256 _debt, uint256 _price) external pure returns (uint256) {
		return ZebraMath._computeCR(_coll, _debt, _price);
	}
}

contracts/helpers/MultiTroveGetter.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../interfaces/ITroveManager.sol";
import "../interfaces/ISortedTroves.sol";
import "../interfaces/IFactory.sol";

/*  Helper contract for grabbing Trove data for the front end. Not part of the ZebraCore Zebra system. */
contract MultiTroveGetter {
	struct CombinedTroveData {
		address owner;
		uint256 debt;
		uint256 interest;
		uint256 coll;
		uint256 stake;
		uint256 pendingCollateral;
		uint256 pendingDebt;
	}

	function getMultipleSortedTroves(ITroveManager troveManager, int _startIdx, uint256 _count) external view returns (CombinedTroveData[] memory _troves) {
		ISortedTroves sortedTroves = ISortedTroves(troveManager.sortedTroves());
		uint256 startIdx;
		bool descend;

		if (_startIdx >= 0) {
			startIdx = uint256(_startIdx);
			descend = true;
		} else {
			startIdx = uint256(-(_startIdx + 1));
			descend = false;
		}

		uint256 sortedTrovesSize = sortedTroves.getSize();
		if (startIdx >= sortedTrovesSize) {
			_troves = new CombinedTroveData[](0);
		} else {
			uint256 maxCount = sortedTrovesSize - startIdx;
			if (_count > maxCount) {
				_count = maxCount;
			}

			if (descend) {
				_troves = _getMultipleSortedTrovesFromHead(troveManager, sortedTroves, startIdx, _count);
			} else {
				_troves = _getMultipleSortedTrovesFromTail(troveManager, sortedTroves, startIdx, _count);
			}
		}
	}

	function _getMultipleSortedTrovesFromHead(ITroveManager troveManager, ISortedTroves sortedTroves, uint256 _startIdx, uint256 _count) internal view returns (CombinedTroveData[] memory _troves) {
		address currentTroveowner = sortedTroves.getFirst();

		for (uint256 idx = 0; idx < _startIdx; ++idx) {
			currentTroveowner = sortedTroves.getNext(currentTroveowner);
		}

		_troves = new CombinedTroveData[](_count);

		for (uint256 idx = 0; idx < _count; ++idx) {
			_troves[idx].owner = currentTroveowner;

			ITroveManager.Trove memory trove = troveManager.getTrove(currentTroveowner);
			_troves[idx].debt = trove.debt;
			_troves[idx].coll = trove.coll;
			_troves[idx].stake = trove.stake;
			(_troves[idx].pendingCollateral, _troves[idx].pendingDebt) = troveManager.getPendingCollAndDebtRewards(currentTroveowner);
			_troves[idx].interest = troveManager.getTroveInterest(currentTroveowner, _troves[idx].debt + _troves[idx].pendingDebt);
			currentTroveowner = sortedTroves.getNext(currentTroveowner);
		}
	}

	function _getMultipleSortedTrovesFromTail(ITroveManager troveManager, ISortedTroves sortedTroves, uint256 _startIdx, uint256 _count) internal view returns (CombinedTroveData[] memory _troves) {
		address currentTroveowner = sortedTroves.getLast();

		for (uint256 idx = 0; idx < _startIdx; ++idx) {
			currentTroveowner = sortedTroves.getPrev(currentTroveowner);
		}

		_troves = new CombinedTroveData[](_count);

		for (uint256 idx = 0; idx < _count; ++idx) {
			_troves[idx].owner = currentTroveowner;
			ITroveManager.Trove memory trove = troveManager.getTrove(currentTroveowner);
			_troves[idx].debt = trove.debt;
			_troves[idx].coll = trove.coll;
			_troves[idx].stake = trove.stake;

			(_troves[idx].pendingCollateral, _troves[idx].pendingDebt) = troveManager.getPendingCollAndDebtRewards(currentTroveowner);

			_troves[idx].interest = troveManager.getTroveInterest(currentTroveowner, _troves[idx].debt + _troves[idx].pendingDebt);

			currentTroveowner = sortedTroves.getPrev(currentTroveowner);
		}
	}
}

contracts/helpers/TroveManagerGetters.so.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../interfaces/ITroveManager.sol";
import "../interfaces/IFactory.sol";

/*  Helper contract for grabbing Trove data for the front end. Not part of the ZebraCore Zebra system. */
contract TroveManagerGetters {
	struct Collateral {
		IERC20 collateral;
		address[] troveManagers;
	}

	IFactory public immutable factory;

	constructor(IFactory _factory) {
		factory = _factory;
	}

	/**
        @notice Returns all active system trove managers and collaterals, as an
        `       array of tuples of [(collateral, [troveManager, ...]), ...]
     */
	function getAllCollateralsAndTroveManagers() external view returns (Collateral[] memory) {
		uint256 length = factory.troveManagerCount();
		address[2][] memory troveManagersAndCollaterals = new address[2][](length);
		address[] memory uniqueCollaterals = new address[](length);
		uint256 collateralCount;
		for (uint i = 0; i < length; i++) {
			address troveManager = factory.troveManagers(i);
			IERC20 collateral = ITroveManager(troveManager).collateralToken();
			troveManagersAndCollaterals[i] = [troveManager, address(collateral)];
			for (uint x = 0; x < length; x++) {
				if (uniqueCollaterals[x] == address(collateral)) break;
				if (uniqueCollaterals[x] == address(0)) {
					uniqueCollaterals[x] = address(collateral);
					collateralCount++;
					break;
				}
			}
		}
		Collateral[] memory collateralMap = new Collateral[](collateralCount);
		for (uint i = 0; i < collateralCount; i++) {
			collateralMap[i].collateral = IERC20(uniqueCollaterals[i]);
			uint tmCollCount = 0;
			address[] memory troveManagers = new address[](length);
			for (uint x = 0; x < length; x++) {
				if (troveManagersAndCollaterals[x][1] == uniqueCollaterals[i]) {
					troveManagers[tmCollCount] = troveManagersAndCollaterals[x][0];
					tmCollCount++;
				}
			}
			collateralMap[i].troveManagers = new address[](tmCollCount);
			for (uint x = 0; x < tmCollCount; x++) {
				collateralMap[i].troveManagers[x] = troveManagers[x];
			}
		}

		return collateralMap;
	}

	/**
        @notice Returns a list of trove managers where `account` has an existing trove
     */
	function getActiveTroveManagersForAccount(address account) external view returns (address[] memory) {
		uint256 length = factory.troveManagerCount();
		address[] memory troveManagers = new address[](length);
		uint256 tmCount;
		for (uint i = 0; i < length; i++) {
			address troveManager = factory.troveManagers(i);
			if (ITroveManager(troveManager).getTroveStatus(account) > 0) {
				troveManagers[tmCount] = troveManager;
				tmCount++;
			}
		}
		assembly {
			mstore(troveManagers, tmCount)
		}
		return troveManagers;
	}
}

contracts/helpers/TroveState.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../interfaces/IBorrowerOperations.sol";
import "../interfaces/ITroveManager.sol";
import "../interfaces/ISortedTroves.sol";
import "../interfaces/IStabilityPool.sol";

contract TroveState {
	IBorrowerOperations public bo;
	ITroveManager public tm;
	IStabilityPool public sp;

	struct State {
		uint256 coll;
		uint256 debt;
		uint256 interest;
		uint256 MCR;
		uint256 ICR;
		uint256 CCR;
		uint256 TCR;
		uint256 price;
		uint256 maxCap;
		uint256 totalUSDMinted;
		uint256 spStaked;
		uint256 spWETHGains;
		uint256 spEsZebraGains;
	}

	constructor(IBorrowerOperations _bo, ITroveManager _tm, IStabilityPool _sp) {
		bo = _bo;
		tm = _tm;
		sp = _sp;
	}

	function getState(address _borrower) public returns (State memory state) {
		(state.coll, state.debt) = tm.getTroveCollAndDebt(_borrower);
		state.interest = tm.getTroveInterest(_borrower, state.debt);
		state.MCR = tm.MCR();
		state.CCR = tm.CCR();
		state.TCR = bo.getTCR();
		state.price = tm.fetchPrice();
		state.totalUSDMinted = tm.getEntireSystemDebt();
		state.spStaked = sp.getTotalZebraUSDDeposits();
		uint256[] memory collGains = sp.getDepositorCollateralGain(_borrower);
		if (collGains.length > 0) {
			state.spWETHGains = collGains[0];
		}
		state.spEsZebraGains = sp.claimableReward(_borrower);
	}
}

