interface ITroveManager is IZebraBase {
	// Store the necessary data for a trove
	struct Trove {
		uint256 debt;
		uint256 coll;
		uint256 stake;
		Status status;
		uint128 arrayIndex;
	}

	struct RedemptionTotals {
		uint256 remainingDebt;
		uint256 totalDebtToRedeem;
